INSERT INTO `database` (`id`, `familyname`, `name`, `scientificname`, `anothername`, `nature`, `blade`, `flower`, `result`, `floweringandfruitingtime`, `utilization`, `color`) VALUES
(1, 'Acanthaceae', 'หูปากกา', 'Thunbergia fragrans Roxb.', 'จิงง้อ จึงจ๋อเขาตาชืแป้น (สระบุรี) ของหูปากกา (ประจวบคีรีขันธ์), ทองหูปากกา (สุราษฎร์ธานี)  หนามแน่ขาว (ภาคเหนือ)', ' ไม้เถาเลื้อยพาดพันกับต้นไม้อื่น มีความยาวประมาณ 1-3 เมตร', 'ใบเดี่ยว เรียงตรงข้ามกัน รูปไข่แกมรูปใบหอก ขอบใบหยักตื้นๆ แผ่นใบบาง มีขนตามเส้นใบ ผิวใบด้านบนเรียบ ส่วนด้านล่างเรียบหรือมีขน', 'ดอกเดี่ยว ออกตามซอกใบ สีขาว กลางดอกเป็นสีเหลือง มีกลิ่นหอม กลีบดอก 5 กลีบ ปลาย ดอก กลีบดอกเว้าโคนเชื่อมติดกันเป็นรูปปากแตร มีขนาดเส้นผ่านศูนย์กลางประมาณ 2-3 เซนติเมตร', 'ผลมีลักษณะกลม ปลายผลเป็นจะงอยแหลมแข็ง ผลแห้งแตกออกได้', 'เดือนกรกฎาคม - เดือนตุลาคม', 'ทั้งต้นใช้ผสมกับต้นจันตาปะขาว นำมาต้มกับน้ำดื่มเป็นยาแก้อัมพฤกษ์ อัมพาต', '240.0'),
(2, 'Anacardiaceae', 'มะปราง', 'Bouea macrophylla Griff.', 'มะปราง (ปัตตานี)', 'ไม้ต้น สูงถึง 6-30 เมตร ไม่ผลัดใบหรือผลัดใบในช่วงสั้น เปลือกสีน้ำตาลอมส้มถึงสีน้ำตาล ออกม่วง เรียบหรือแตกเป็นร่องตื้น', 'ใบเดี่ยว กว้าง 5-8 เซนติเมตร ยาว 14-30 เซนติเมตร เส้นใบ 15-25 คู่', 'ดอกช่อ ออกเป็นช่อแขนง สีขาวถึงสีเหลืองอมเขียว ดอกเพศผู้และสมบูรณ์เพศอยู่บนต้นเดียวกัน ออกที่ซอกใบหรือปลายกิ่ง เกสรเพศผู้สมบูรณ์เพศ 3-5 อัน รังไข่เหนือวงกลีบ มี 1 ช่อง มีก้านเกสร เพศเมียสั้นติดตรงกลาง', 'ผลเดี่ยว รูปไข่หรือขอบขนานกว้าง 3-4 มิลลิเมตร ยาว 3.5-5 เซนติเมตร', 'ออกดอกเดือนมกราคม - เดือนเมษายน', 'อาหาร สมุนไพร', '0.0'),
(3, 'Anacardiaceae', 'มะปริง', 'Bouea oppositifolia (Roxb.) Meisn.', 'โค้ง (เขมร-สุรินทร์), มะปริง (ชุมพร), มะผาง (เหนือ), มะยง มะยงชิด (กลาง), สะตา (มาเลย์-ใต้)', 'ไม้ต้น สูงถึง 6-30 เมตร ไม่ผลัดใบหรือผลัดใบในช่วงสั้น เปลือกสีน้ำตาลอมส้มถึงสีน้ำตาล ออกม่วง เรียบหรือแตกเป็นร่องตื้น', 'ใบเดี่ยว ออกตรงข้าม แผ่แบน รูปรีถึงรูปใบหอกแกมขอบขนาน ปลายเรียวแหลม พบน้อยที่ ปลายทู่ โคนแหลม ขอบเรียบ ใบอ่อนสีม่วง ใบแก่คล้ายหนัง สีเขียวเข้มขุ่นด้านบน ด้านล่าง สีอ่อนกว่าหรือสีออกเทาเกลี้ยงทั้งสองด้าน มีกลิ่นในเมื่อขยี้ใบ', 'ดอกช่อ ออกเป็นช่อแขนงยาวถึง 25 เซนติเมตร ดอกยาว 3 มิลลิเมตร สีขาวถึงสีเหลืองอมเขียว ดอกเพศผู้และสมบูรณ์เพศอยู่บนต้นเดียวกันออกที่ซอกใบหรือปลายกิ่ง เกสรเพศผู้สมบูรณ์เพศ 3-5 อัน รังไข่เหนือวงกลีบ มี 1 ช่อง มีก้านเกสรเพศเมียสั้นติดตรงกลาง', 'ผลเดี่ยว รูปไข่หรือรูปไข่กลับ ยาว 2.5-3.5 เซนติเมตร สุกสีเหลืองถึงส้ม เกลี้ยง เปลือกบาง ผล มีเนื้อ ผนังผลชั้นในแข็ง ภายในมี 1 เมล็ด เนื้อนุ่มกินได้', 'เดือนกรกฏาคม - เดือนสิงหาคม', 'ผลสุกกินได้ เนื้อไม้สีน้ำตาลเข้ม แข็งแรงทนทาน', '240.0'),
(4, 'Ancistrocladaceae', 'ค้อนตีหมา', 'Ancistrocladus tectorius (Lour.) Merr', 'กระม้า (เขมร-สระบุรี), ขุนมา (เขมร-สุรินทร์), ค้อนหมาขาว (ภาคกลาง), ค้อนหมาแดง (นครราชสีมา), คันทรง (ชลบุรี), โคนมะเด็น (สุพรรณบุรี), ซินตะโกพลี (กะเหรี่ยง-ลำปาง) ทองคันทรง (ชลบุรี), พันทรง (นราธิวาส), ยูลง ลิดาซาปี (มลายู-ภาคใต้), ลิ้น ลิ้นกวาง ลิ้นควาย(ลำปาง), หางกวาง (นครพนม), หูกลวง (ตราด, ปราจีนบุรี)', 'ไม้เถาเลื้อยเนื้อแข็งขนาดใหญ่ ลำต้นขึ้นใหม่เป็นพุ่ม มักเลื้อยพาดพันไปตามต้นไม้อื่น', 'ใบเดี่ยว ออกเป็นกระจุกที่ปลายกิ่ง เรียงเวียนสลับ รูปหอกกลับ รูปรี หรือรูปขอบขนานแกมรูปหอก ปลายใบแหลม โคนใบเรียวแหลมและค่อยๆ สอบเข้าหาก้านใบ ส่วนขอบใบเรียบหรือเป็นคลื่น แผ่นใบแข็งกระด้าง มีเส้นใบเชื่อมกันก่อนถึงขอบใบ ยอดอ่อนเป็นสีแดงหรือสีเขียวอมขาวอ่อนๆ', 'ดอกเป็นช่อกระจะที่ปลายยอด แต่ละดอกจะมีขนาดเล็ก ฐานดอกเป็นสีเขียว ส่วนกลีบดอก เป็นสีขาวอมแดงถึงสีแดงคล้ำ ส่วนโคนเป็นท่อสั้นๆ แยกออกเป็น 5 กลีบ', 'ผลมีปีกอยู่ 5 ปีก ซึ่งจะมีความยาวไม่เท่ากันรองรับ แบ่งเป็นปีกเล็ก 2 ปีก และปีกใหญ่ 3 ปีก เมื่อแก่จะแห้งเป็นสีน้ำตาล', 'เดือนกุมภาพันธ์ - เดือนเมษายน', 'ใบอ่อนนำมาต้มเอาน้ำอาบเป็นยารักษาอาการบวมตามตัวและเม็ดผื่นคันตามผิวหนัง ใบอ่อน หรือยอดอ่อนรับประทานเป็นผักสดจิ้มกับน้ำพริกรวมกับผักอื่นๆ มีรสฝาดมัน รากใช้ผสมกับ รากช้างน้าว นํามาต้มกับนํ้าดื่มเป็นยาแก้ปวดเมื่อย', '0.0'),
(5, 'Annoaceae', 'กล้วยมูสัง', 'Uvaria grandiflora Roxb. Ex Hornem, var. grandiflora', 'กล้วยมุดสัง, กล้วยมดสัง, ย่านนมควาย (ตรัง), กล้วยมูซัง (สงขลา)', 'ใบเดี่ยว เรียงสลับ รูปหอกแกมรูปไข่กลับ รูปรีแกมรูปขอบขนาน หรือรูปขอบขนาน แกนใบหอก ปลายแหลม โคนมนเว้าเล็กน้อย ขอบเรียบ แผ่นใบด้านบนสีเขียวเข้มเป็นมัน มีขนประปราย 10 ตามเส้นกลางใบและเส้นแขนง ด้านล่างสีเขียวนวล มีขนนุ่มสั้นทั่วไป', 'ใบเดี่ยว เรียงสลับ รูปหอกแกมรูปไข่กลับ รูปรีแกมรูปขอบขนาน หรือรูปขอบขนาน แกนใบหอก ปลายแหลม โคนมนเว้าเล็กน้อย ขอบเรียบ แผ่นใบด้านบนสีเขียวเข้มเป็นมัน รียนประปราย ตามเส้นกลางใบและเส้นแขนง ด้านล่างสีเขียวนวล มีขนนุ่มสั้นทั่วไป', 'ช่อสีเหลือง ออกตามเถาและซอกใบ แยกเพศต่างดอก ไม่มีกลีบดอก', 'รูปรี เมล็ดแข็ง', '-', 'รากแก้ไข้ เป็นยาแก้ท้องผูก แก้เบื่อเมา ใบถอนพิษ แก้ไข้ ลดความร้อน แก้พิษตานซาง เถาแก้ไข้ ลดความร้อน แก้พิษตาลชาง', '240.0'),
(6, 'Annoaceae', 'นมควาย', 'Uvaria rufa Blume', 'ตึงตัง (นครราชสีมา), ตีนตั้งเครือ (ศรีษะเกษ), นมควาย (ทั่วไป), นมแมว (กลาง), นมแมวป่า (เชียงใหม่), นมวัว (พิษณุโลก), บุหงาใหญ่ (เหนือ), พี่พวนน้อย (ชุมพร)', 'ไม้พุ่มหรือรอเลื้อย มีขนรูปดาวสั้นนุ่มตามกิ่ง', 'ใบเดี่ยว เรียงสลับ รูปรีหรือรูปขอบขนาน ยาว 5-15 เซนติเมตร ปลายแหลมหรือแหลมยาว โคนเว้าตื้น ก้านใบยาว 0.5-1 เซนติเมตร', 'ช่อดอกออกตรงข้ามใบหรือซอกใบใกล้ปลายกิ่ง ส่วนมากมี 1-2 ดอก ใบประดับรูปไข่ยาวเท่าๆ กลีบเลี้ยง ร่วงเร็ว ดอกบานเส้นผ่านศูนย์กลาง 1.5-2.5 เซนติเมตร ก้านดอกยาว 1-2 เซนติเมตร กลีบเลี้ยง 3 กลีบ ยาวประมาณ 5 มิลลิเมตร ดอกสีม่วงอมแดง กลีบยาวเท่าๆ กัน รูปไข่ วงนอก 3 กลีบ วงใน 3 กลีบ ยาว 0.8-1.2 เซนติเมตร เกสรเพศผู้รูปขอบขนานปลายอับเรณูมีรยางค์  คาร์เพลแยกกัน รูปขอบขนาน', 'ผลย่อยรูปรีหรือรูปไข่ ยาว 2-4 เซนติเมตร สุกสีแดงอมส้ม', 'ผลย่อยรูปรีหรือรูปไข่ ยาว 2-4 เซนติเมตร สุกสีแดงอมส้ม', 'แก่นและรากต้มน้ำดื่ม แก้ไข้ สลับไข้ซ้ำ เนื่องจากกินของแสลง รากแก้ผอมแห้งสําหรับ สตรีที่อยู่ไฟไม่ได้หลังการคลอดบุตร และช่วยบำรุงน้ำนม ผลสุก รสเปรี้ยวๆ รับประทานได้ ต้องปอกเปลือกออก และทานทั้งเมล็ด เนื่องจากเนื้อและเมล็ดติดกัน เถานมควาย (ภาษาไทยถิ่นใต้ เรียกว่า ย่านนมควาย) มีเนื้อไม้แข็งสามารถนำมาทำเฟอร์นิเจอร์ตกแต่งบ้านหรือใช้ในงาน จักสานได้', '0.0'),
(7, 'Annoaceae', 'นมแมว', 'Uvaria siamensis (Scheff.)', '-', 'ไม้พุ่มหรือรอเลื้อย มีขนกระจุกสั้นนุ่มสีน้ำตาลแดงตามกิ่งอ่อน', 'ใบเดี่ยว เรียงสลับ รูปรีถึงรูปใบหอก ยาว 9-15 เซนติเมตร ก้านใบยาว 3-5 มิลลิเมตร แผ่นใบ ด้านล่างและเส้นกลางใบด้านบน มีขนสั้นนุ่มตามก้านใบ', 'ช่อดอกออกตรงข้ามใบ มี 1-3 ดอก ก้านช่อสั้น ก้านดอกยาวประมาณ 8 มิลลิเมตร ใบประดับ รูปไข่ยาว 2-5 มิลลิเมตร ร่วงเร็ว กลีบเลี้ยงหนา รูปไข่กว้าง ยาวประมาณ 3 มิลลิเมตร ดอก สีเหลืองนวล กลีบรูปไข่ ยาวประมาณ 8 มิลลิเมตร วงในแคบกว่าเล็กน้อย โคนคอดเรียวเกลี้ยง เกสรเพศผู้ยาวประมาณ 1.5 มิลลิเมตร มีประมาณ 10 คาร์เพล มีขนกระจุกคล้ายเกล็ด', 'ผลย่อยมี 2-10 ผล รูปรีกว้าง ยาว 1-2 เซนติเมตร ส่วนมากมี 2-4 เมล็ด แบน ยาวประมาณ ษณะวิสัย 7 มิลลิเมตร', 'ออกดอกและเป็นผลเกือบตลอดปี', 'ยาพื้นบ้านอีสานใช้ รากตำผสมน้ำปูนใสทาแก้พิษแมลงกัดต่อย', '240.0'),
(8, 'Annoaceae', 'สายหยุด', 'Desmos chinensis Lour.', 'กล้วยเครือ (สระบุรี), เครือเขาแกลบ (เลย), สาวหยุด (ภาคกลาง), เสลาเพชร (สุราษฎร์ธานี)', 'ไม้เถาเนื้อไม้แข็ง มีความยาวหรือความสูงประมาณ 1-5 เมตร', 'ใบเดี่ยว ออกเรียงสลับ รูปขอบขนาน หรือรูปขอบขนานแกมรูปใบหอก ปลายใบเรียวแหลม โคนใบมนหรือเว้าเล็กน้อย ใบกว้างประมาณ 3-7 เซนติเมตร และยาวประมาณ 6-15 เซนติเมตร ใบอ่อนเป็นสีแดง มีเส้นแขนงใบประมาณ 8-10 คู่ มีก้านใบยาวประมาณ 5 มิลลิเมตร', 'ดอกเดี่ยว ออกด้านล่างตรงข้ามกับใบ เริ่มออกเป็นสีเขียวและจะเปลี่ยนเป็นสีเหลืองถึงสีส้มอ่อน ดอก มีกลิ่นหอม และบานอยู่ได้นาน', 'ผลกลุ่ม มีประมาณ 5-35 ผลย่อยคล้ายลูกปัดคอด กว้าง 5 เซนติเมตร และยาว 2-4 เซนติเมตร ผิวผลเรียบเป็นมัน ผลสดหรืออ่อนเป็นสีเขียว เมื่อสุกเปลี่ยนเป็นสีดำเป็นมัน', 'เดือนพฤษภาคม - เดือนตุลาคม)', 'ดอกสด เข้ายาหอม บำรุงหัวใจ แก้ลมวิงเวียน ต้นและรากรักษาอาการติดยาเสพติด แก้ไข้ แก้ท้องร่วง', '0.0'),
(9, 'Apocynaceae', 'เถาเอ็นอ่อน', 'Cryptolepis dubia (Burm. f.) M. R. Almeida', 'กวน (ฉาน-แม่ฮ่องสอน), เครือเขาเอ็น (เชียงใหม่), ตีนเป็ดเครือ (ภาคเหนือ), นอออหมี (กะเหรี่ยง-แม่ฮ่องสอน), พรมทัต (จันทบุรี), เมื่อย (ภาคกลาง), หญ้าลิเลน (ปัตตานี), หมอนตีนเป็ด (สุราษฎร์ธานี)', 'ไม้เถาเลื้อยพาดพันกับต้นไม้อื่น ยาวประมาณ 4-5 เมตร', 'ใบเดี่ยว เรียงตรงข้ามกัน ใบรูปรีหรือรูปไข่ ปลายใบมนมีหางสั้น โคนใบสอบ ขอบใบเรียบ แผ่นใบค่อนข้างหนา หลังใบเรียบเป็นมันและลื่น ท้องใบเรียบสีเขียวนวล ใบอ่อนมีขนปกคลุม ใบแก่ไม่มีขน เส้นใบตามขวางจะเป็นเส้นตรงไม่โค้ง ก้านใบสั้น', 'ดอกออกช่อตามซอกใบ ดอกย่อยสีเหลืองอ่อนหรือสีเป็นสีขาวอมเหลือง กลีบดอก 5 กลีบ  โคนกลีบดอกเชื่อมติดกัน กลีบเลี้ยงสีเขียว 5 กลีบ', 'ผลเป็นฝัก รูปทรงกระสวย กลมยาว มีเนื้อแข็ง โคนผลติดกัน ปลายแหลม ผิวเป็นมันลื่น แก่แตกอ้าออก เมล็ดสีน้ำตาลมีขนปุยสีขาวติดอยู่และปลิวไปตามลมได้ ยาวแบน', 'เดือนมีนาคม - เดือนเมษายน', 'ราก เถา และใบมีรสขมเบื่อเอียน เป็นยาเย็น มีพิษ ออกฤทธิ์ต่อหัวใจและตับ ใช้เป็นยาฟอกเลือด เมล็ดมีรสขมเมา เป็นยาขับลมในลำไส้และในกระเพาะอาหาร ใบและเถาเป็นยาบำรุงเส้นเอ็น แก้อาการปวดเมื่อย', '240.0'),
(10, 'Apocynaceae', 'โมกเครือ', 'Amphineurion marginatum (Roxb.) D. J. Middleton', 'เครือไส้ตัน (หนองคาย), เมื่อเครือ เดือดิน เมื่อเถา เมื่อไม้ (ภาคเหนือ), เดือยดิน (ประจวบ คีรีขันธ์), เดือยดิบ (กระบี่), ตะซื้อบลาโก๊ะ (กะเหรี่ยง-แม่ฮ่องสอน), พิษ (ภาคกลาง), มะเดื่อดิน (ทั่วไป), มะเดื่อเถา (ภาคเหนือ, ราชบุรี), ย่านเดือยบิด (สุราษฎร์ธานี), ไส้ตัน (นครราชสีมา)', 'ไม้เถาเนื้อแข็งขนาดเล็กถึงขนาดกลาง เปลือกสีน้ำตาลเข้ม ทุกส่วนของลำต้นมีน้ำยางขาวและ เหนียว มีตุ่มตามลำต้น', 'ใบเดี่ยว เรียงตรงข้าม รูปขอบขนาน หรือรูปรี ปลายใบแหลม โคนใบสอบ ขอบใบเป็นคลื่น ยอดอ่อนสีนํ้าตาลแดง แผ่นใบเป็นคลื่น สีเขียวเข้มเป็นมันวาว หลังใบเกลี้ยงหรือมีขนประปราย ท้องใบมีขนสั้นๆ ใบแก่เกลี้ยงสีเขียวเข้มเป็นมัน', 'ดอกช่อกระจะออกที่ปลายกิ่ง กลีบเชื่อมติดกันเป็นรูปกรวย ปลายกลีบแยก ดอกย่อยสีขาวเกลี้ยง กลีบดอกมี 5 กลีบ เรียงเวียนกัน ดอกตูมกลีบออกบิดไปทางเดียวกัน กลีบเลี้ยงมี 5 กลีบ รูปรี แกมรูปหอก สีเขียวอ่อน โคนเชื่อมติดกัน เกสรเพศผู้มี 5 อัน ก้านเกสรติดกับอับเรณู ด้านฐาน บริเวณโคนก้านมีกระจุกขน เกสรเพศเมียมีรังไข่เหนือวงกลีบ มี 2 ห้อง แยกกัน', 'ผลแห้งแตกตามยาวเป็นตะเข็บเดียว ฝักคู่ เรียวยาว ทรงกลม ปลายแหลม เมล็ดสีน้ำตาล 35-62 เมล็ด มีขนสีขาวเป็นพี่ติดอยู่)', 'เดือนมีนาคม - เดือนกรกฎาคม', 'เถามีรสเพื่อนฝาด ใช้ทาฝี แก้เมื่อยขบ แก้ผดผื่นคัน นำมาผสมกับผลมะตูมอ่อน เถาสิงโต และ ว่านมหากาฬ ต้มน้ำดื่มรักษาโรคเบาหวาน ยอดอ่อนรับประทานเป็นผักสด', '0.0'),
(11, 'Araceae', 'เอาะนก', 'Alocasia longiloba Miq.', 'กะเจาะนก (ระนอง), แก้วหน้าม้า (ทั่วไป), บอนเปรี้ยว (ตราด), เอาะนก (ชุมพร)', 'ไม้ล้มลุก มีเหง้าทอดไปตามพื้นดิน ลำต้นตั้งตรง', 'ใบเดี่ยวรูปหอก แกมรูปหัวใจ ปลายแหลม โคนเว้าลึก แคบ ขอบเรียบหรือเป็นคลื่น เส้นแขนงใบ ทำมุมรูปสามเหลี่ยม สามเส้น', 'ช่อเป็นแท่งยาวปลายแหลม ก้านช่อดอกเล็ก ดอกมีกาบสีเหลืองอมเขียวหุ้ม โคนของกาบโอบรอบ  โคนช่อ', 'ผลสดมีเนื้อนุ่ม รูปค่อนกลมสีส้มถึงแดงจำนวนมาก หุ้มด้วยกาบหุ้มที่เปลี่ยนจากกาบหุ้มช่อดอก กาบหุ้มช่อผลฉีกลอกออกตามแนวยาว เมล็ด 1-3 เมล็ด ค่อนข้างกลม', '-', 'ไม้ดอกไม้ประดับ', '240.0'),
(12, 'Araceae', 'เต่าร้าง', 'Caryota mitis Lour', 'เมืองหมู่ (เหนือ), ซื้อเด็ง (มาเลย์-นราธิวาส), เต่าร้างแดง (นครศรีธรรมราช), มะเส็ง (ยะลา', 'ปาล์มแตกกอ สูงได้ถึง 10 เมตร ลำต้นเส้นผ่านศูนย์กลางโตได้ถึง 20 เซนติเมตร', 'ใบประกอบสองชั้น ชั้นที่สองข้างละ 10-20 ใบ รูปสามเหลี่ยมเรียวแหลม ปลายจัก ยาว 20-30 เซนติเมตร บางครั้งแฉกลึกจรดโคน 2-3 แฉก คล้ายหางปลา ก้านใบยาว 0.8-2 เมตร แกนกลาง ใบประกอบยาว 2-2.8 เมตร', 'ช่อดอกโค้งลง ยาวได้ถึง 85 เซนติเมตร แกนช่อยาว 20-65 เซนติเมตร ช่อย่อยจำนวนมาก ดอกเพศผู้กลีบดอกยาว 1.2-1.5 เซนติเมตร ดอกเพศเมียกลีบดอกยาว 4-5 มิลลิเมตร', 'ผลเส้นผ่านศูนย์กลาง 1-2 เซนติเมตร สุกสีม่วงดำ ส่วนมากมีเมล็ดเดียว เนื้อในเมล็ดเป็นชั้น', 'เกือบตลอดปี', 'เป็นไม้ประดับ เปลือกผลทำให้ระคายเคือง เส้นใยกาบประคบแผลสดห้ามเลือด', '0.0'),
(13, 'Araceae', 'พ้อ', 'Licuala spinosa Thunb.', 'กระพ้อ (กลาง), กระพ้อเขียว (ใต้), กระพ้อหนาม (กลาง), กูวา (มาเลย์-นราธิวาส) พ่อ (ใต้)', 'ปาล์มลำต้นเตี้ยถึงขนาดกลาง สูง 3-4 เมตร เส้นผ่าศูนย์กลาง 5-10 เซนติเมตร ลำต้นแตกหน่อ เป็นกอแน่น มีรอบกาบใบที่หล่นไป', 'ใบประกอบแบบนิ้วมือ เรียงเวียน รูปลิ่ม กว้างและยาวประมาณ 1 เมตร ก้านใบยาว 0.8-1 เมตร ก้านใบและกาบใบมีหนามเล็กๆ ห่างๆ กลุ่มใบย่อย 15-27 กลุ่ม ใบย่อยรูปแถบ โคนสอบปลายตัด บางใบเว้าเป็นหางปลา ออกเป็นกลุ่มชิดกันที่ปลายก้านใบ', 'ช่อดอกแบบแยกแขนง ห้อยลง ยาวประมาณ 2 เมตร แยกแขนงเป็น 3-4 ช่อ ช่อย่อย แบบช่อเชิงลด ออกเป็นระยะห่างๆ กัน มีกาบเป็นหลอดยาว ออกเดี่ยวหรือเป็นกลุ่ม 2-3 ดอก ใบประดับรูปสามเหลี่ยมเล็กๆ กลีบเลี้ยงรูปถ้วยปลายตัดสั้นมาก กลีบดอกยาวกว่ากลีบเลี้ยง เกสรเพศผู้ 6 อัน เกสรเพศเมียเล็กมาก รังไข่อยู่เหนือวงกลีบมี 1-3 ช่อง มีออวุล 1 เม็ด', 'ผลรูปกระสวย แก่จัดสีแสดอมแดง เนื้อบาง เมล็ดกลม เส้นผ่านศูนย์กลางประมาณ 1 เซนติเมตร', 'ออกดอกและผลเกือบตลอดปี', 'ยอดอ่อนลอกออกแล้วนำมาต้มเป็นผักจิ้ม ใบนำมาห่อข้าวเหนียว ห่อขนมต้ม', '240.0'),
(14, 'Aristolochiaceae', 'กระเช้าผีมด', 'Aristolochia tagala Cham.', 'กระเช้ามด กระเช้าสีดา (ภาคกลาง), ปุลิง (เชียงใหม่), หาบกะเชอ (ขอนแก่น)', 'ไม้เถาเลื้อย ผิวเป็นร่องไปตามยาวของลำเถา ลำต้นเมื่อยังอ่อนจะมีขน', 'ใบเดี่ยว เรียงสลับ ตั้งแต่รูปไข่ไปจนถึงรูปไข่แกมรูปใบหอก ปลายใบแหลม โคนใบเว้าลึกเป็นรูป หัวใจหรือรูปติ่งหู ส่วนขอบใบเรียบ ใบที่เจริญเติบโตเต็มที่แล้วจะเกลี้ยงทั้งสองด้าน แผ่นใบ จะมีต่อมลักษณะเป็นจุดเล็กๆ กระจัดกระจาย ', 'ดอกช่อ สมบูรณ์เพศ กลีบดอกชั้นเดียวเชื่อมติดกันเป็นหลอด ใบประดับรูปไข่ ปลายและโคน ของใบประดับแหลม เกสรเพศผู้มี 6 อัน อยู่แนบติดกับก้านเกสรเพศเมีย อับเรณูเป็นรูปรี ยอดเกสรเพศเมียแยกออกเป็นแฉก 6 แฉก เป็นรูปกรวยยาว ปลายมน รังไข่อยู่ใต้วงกลีบ รูปทรงกระบอกเล็กๆ', 'ผลค่อนข้างกลม รูปไข่ หรือเป็นรูปขอบขนานแกมรี เมื่อแก่จะแตกออกตามยาวจากขั้วไปยังโคน และก้านผลก็จะแตกแยกออกเป็น 6 เส้น โคนก้านและปลายผลยังติดกันอยู่ มีลักษณะรูปร่าง คล้ายกระเช้า ภายในผลมีเมล็ดหลายเมล็ด', 'เดือนกุมภาพันธ์ - เดือนพฤษภาคม', 'มีฤทธิ์ต้านเชื้อแบคทีเรีย ต้นใช้กินเป็นยาบำรุงธาตุ ใบใช้ตำเป็นยาพอกภายนอก', '0.0'),
(15, 'Asteraceae', 'สาบเสือ', 'Chromolaena odorata (L.)', 'ช้าผักคราด ยี่สุ่นเถื่อน (สุราษฎร์ธานี), ชิโพกวย ใช้ปู่กอ เซโพกวย บ่อโส่ เพาะจีแค (กะเหรี่ยง- แม่ฮ่องสอน), บ้านร้าง ผักคราด (ราชบุรี), เบญจมาศ (ตราด), ฝรั่งรุกที่ ฝรั่งเหาะ (สุพรรณบุรี) มนทน (เพชรบูรณ์), มุ้งกระต่าย (อุดรธานี), รำเคย (ระนอง), หญ้าค่ามั้ง (แม่ฮ่องสอน) หญ้าดงร้าง หญ้าพระศิริไอยสวรรค์ (สระบุรี), หญ้าดอกขาว (ทั่วไป), หญ้าฝรั่งเศส (จันทบุรี, ตราด), หญ้าเมืองวาย หญ้าเมืองช้าง (ภาคเหนือ), หญ้าลืมเมือง (หนองคาย), หญ้าเลาช้าง (ขอนแก่น), หญ้าเหม็น (อีสาน), หมาหลง (ชลบุรี)', 'ไม้ล้มลุก มีความสูงประมาณ 1-2 เมตร', 'ใบเดี่ยว รูปไข่แกมรูปรีทรงรูปสามเหลี่ยม ปลายใบแหลม ฐานใบกว้าง เรียวสอบเข้าหากัน ขอบใบหยัก สีเขียวอ่อน ผิวทั้งสองด้านมีขนอ่อนปกคลุม ใบและก้านเมื่อนำมาขยี้จะมีกลิ่นแรง คล้ายกลิ่นสาบเสือ', 'ดอกช่อ สีขาวหรือสีฟ้าอมม่วง มีทั้งดอกเพศเมียและดอกสมบูรณ์เพศ ดอกย่อยประมาณ  10-35 ดอก วงนอกจะบานก่อนวงใน กลีบหลอมรวมกันเป็นหลอด', 'ผลขนาดเล็ก รูปร่างคล้ายรูปห้าเหลี่ยม สีน้ำตาลหรือสีดำ มีหนามแข็งบนเส้นของผล ปลายผล ขนสีขาว', 'เดือนพฤศจิกายน - เดือนกุมภาพันธ์', 'ใบใช้ห้ามเลือด ใช้ใบนำมาโขลกและขยี้ แล้วนำมาพอกบริเวณบาดแผล สมานแผล ช่วยป้องกัน การเกิดรอยแผลเป็น', '240.0'),
(16, 'Blechnaceae', 'ลำเท็ง', 'Stenochlaena palustris (Burm. f.) Bedd.', 'นีดิ่ง (มลายู-นราธิวาส), ปรุงสวน ผักยอดแดง (ภาคกลาง), ปากมะดิง (มลายู-ยะลา), ผักกูดแดง ผักกูดมอญ (กรุงเทพฯ), ลำมะเส็ง (นครราชสีมา, ประจวบคีรีขันธ์)', 'เฟินขึ้นบนดิน แล้วเลื้อยอิงอาศัยตามลำต้นของต้นไม้หรือโขดหิน', 'ใบประกอบแบบขนนก เรียงสลับ ใบย่อยมีจำนวน 20-30 รูปเรียวแคบ ก้านสั้น ใบย่อยกว้าง 2.5-3.5 เซนติเมตร ยาว 13-17 เซนติเมตร แผ่นใบเรียบ ผิวใบเรียบ ขอบเป็นกระจักละเอียด ลักษณะคล้ายหนามแหลม ปลายใบแหลม โคนใบสอบเรียว เส้นกลางใบมีลักษณะกลม เมื่อแก่ จะมีสีน้ำตาล ใบอ่อนสีน้ำตาลแดง ใบแก่สีเขียวก้านใบยาว 40-50 เซนติเมตร ก้านใบย่อยยาว 0.5-0.8 เซนติเมตร กลุ่มอับสปอร์เกิดตรงขอบใบ โคนใบม้วนขึ้นด้านบน ด้านล่างมีอับสปอร์ สีน้ำตาลเป็นเม็ดกลมๆ จำนวนมาก สีน้ำตาลเกาะติดอยู่อัดกันแน่น', '-', '-', '-', 'ยอดอ่อนประกอบอาหาร', '0.0'),
(17, 'Capparaceae', 'กุ่ม', 'Crateva magna (Lour.) DC.', 'กุ่ม (เลย), รอถะ (เชียงใหม่), เหาะเถาะ (กะเหรี่ยง-กาญจนบุรี), อำเภอ (สุพรรณบุรี)', 'ไม้ยืนต้น ไม่ผลัดใบหรือผลัดใบช่วงสั้น สูงได้ถึง 20 เมตร เปลือกสีเทาอ่อน', 'ใบประกอบแบบนิ้วมือ 3 ใบย่อย ออกเรียงสลับ รูปวงรีหรือรูปไข่ ปลายกว้างแหลม โคนสอบแคบ ขอบใบเรียบ ผิวใบบางและนิ่ม หลังใบสีเขียวเป็นมัน ท้องใบมีขนอ่อนสีเทา เส้นกลางใบอมแดง ใบที่อยู่ด้านข้างโคนจะเบี้ยวเล็กน้อย', 'ดอกช่อ ออกที่ปลายกิ่งและซอกใบ มี 20-100 ดอก กลีบดอกแรกบานสีขาวแล้วเปลี่ยนเป็น สีเหลืองอ่อน กลีบรองดอกรูปไข่ปลายแหลม สีขาว มี 4 กลีบ กลีบล่างและกลีบบนอย่างละ 2 กลีบ แต่กลีบบนจะใหญ่กว่า เกสรเพศผู้มีก้านสีม่วงหรือสีชมพู ยาวยื่น 13-25 อัน', 'ผลรูปร่างกลมรี เปลือกหนา อ่อนผิวมีสะเก็ดสีเหลืองอมเทา เมื่อสุกสีเทา แก่ผิวเรียบยาว 5-8 เซนติเมตร เมล็ดรูปเกือกม้า มีหลายเมล็ด มีสีน้ำตาลเข้ม', 'เดือนกุมภาพันธ์ - เดือนมิถุนายน', 'เปลือกต้นใช้ปรุงเป็นยาบำรุงร่างกาย แก้ไข้ ใบเป็นยาเจริญอาหาร ยาระบาย ขับพยาธิ ดอก แก้เจ็บคอ', '240.0'),
(18, 'Cannabaceae', 'พังแหรใหญ่', 'Trema orientalis (L.) Blume', 'ก็กะบะชา, บาเละอาจิง (มาเลย์-นราธิวาส), ขางปอยป่า ปอแฮก (เหนือ), ตะคาย (กลาง) ตายไม่ทันเฒ่า (ยะลา), ปอ (เชียงใหม่), พังแกรใหญ่ (ยะลา), พังแหร (แพร่)', 'ไม้ต้นขนาดเล็กถึงกลาง สูงได้ถึง 18 เมตร เรือนยอดแผ่กว้าง เปลือกสีเขียวอมเทาหรือ ลักษณะวิสัย น้ำตาลอมเทา เกลี้ยงถึงแตกเป็นร่องละเอียด', 'ใบเดี่ยว รูปไข่แคบถึงรูปหอก ปลายเรียวแหลมสั้น โคนกลมรูปหัวใจ อาจเบี้ยวเล็กน้อย ขอบจัก ซี่ฟันละเอียด ยอดอ่อนมีขนสีเงินแน่น ใบแก่คล้ายหนังบางสีเขียวเข้มขุ่น มีขนหยาบกระจาย โป ด้านบน ด้านล่างสีเทาอ่อน มีขนรูปดาว', 'ดอกสีขาวหรือออกเขียว ดอกเพศผู้และดอกเพศเมียอยู่ต่างช่อบนต้นเดียวกัน ดอกเพศผู้กลม ออกเป็นช่อแน่นแตกแขนงตามง่ามใบหรือตามกิ่งใต้ง่ามใบ มักออกเป็นคู่ ช่อหนึ่งมีดอก 20- 100 ดอก ปลายแหลมติดใบประดับ กลีบรวม 4-5 กลีบ รูปใบหอก เกสรเพศผู้ 4-5 อัน ติดตรงกับ กลีบดอก ดอกเพศเมียรูปไข่ออกเป็นช่อหลวม มีดอก 5-20 กลีบรวมคล้ายเพศผู้ เกสรเพศเมีย เล็กเรียวมี 2 ก้าน แยกกันหรือเชื่อมกันที่โคน', 'รูปไข่หรือรูปเลนส์ (แบนเล็กน้อยเมื่อตัดขวาง) มีก้านเกสรเพศเมียเที่ยวติดแน่นที่ปลาย กลีบรวมไม่ขยายใหญ่ขึ้น ที่โคนเกลี้ยง สีเขียวเข้มเมื่อสุกสีจะแดงถึงม่วง', '-', 'เนื้อไม้อ่อนและเบา เลื่อยไสกบตกแต่งง่าย แต่ไม่ทนทาน บางทีใช้ทำไม้ขีดไฟ กล่องชา ใบแก่ ใช้เป็นอาหารสัตว์ ผลเป็นอาหารนก', '0.0'),
(19, 'Clusiaceae', 'ชะมวง', 'Garcinia cowa Roxb. ex Choisy', 'มวงส้ม (นครศรีธรรมราช), กะมวง ม่วง ส้มมวง (ภาคใต้), หมากโมก (อุดรธานี)', 'ไม้ยืนต้นขนาดเล็กถึงกลาง มีความสูงถึง 18 เมตร เรือยยอดแคบ ลำต้นเล็กเรียวแหลม', 'ใบเดี่ยว ออกเรียงสลับตรงข้ามกัน รูปรีแกมหอกหรือแกมขอบขนาน โคนสอบแหลม ปลายบ้าน หรือแหลมเล็กน้อย ขอบเรียบ หลังใบเรียบลื่นเป็นมัน ท้องใบเรียบ เนื้อใบมีลักษณะค่อนข้างหนา และเปราะ เส้นใบเห็นได้ไม่ชัด หลังใบจะเห็นเส้นกลาง ก้านใบเป็นสีแดง', 'มีดอกย่อยประมาณ 3-8 ดอก ออกตามซอกใบและตามกิ่ง ดอกเพศผู้จะออกตามกิ่งเป็นกระจุก มีเกสรเพศผู้เป็นรูปสี่เหลี่ยม กลีบดอกสีเหลืองนวลและมีกลิ่นหอมมี 4 กลีบ กลีบเลี้ยง 4 กลีบ รูปรีแกมรูปขอบขนาน ปลายกลีบกลม ดอกเพศเมียจะออกตามปลายกิ่ง มีเกสรเพศผู้เทียม ดอก เรียงอยู่รอบรังไข่ มีก้านเกสรติดกันเป็นกลุ่ม', 'ผลเป็นรูปทรงกลมแป้น ผิวผลเรียบเป็นมัน ตามผลมีร่องตื้นๆ ประมาณ 5-8 ร่อง', 'เดือนกุมภาพันธ์ - เดือนมิถุนายน', 'ใบและผลรสเปรี้ยว เป็นยาระบาย แก้ไข้ กระหายน้ำ กัดฟอกเสมหะ กัดเสมหะ', '240.0'),
(20, 'Combretaceae', 'แหนนา', 'Terminalia glaucifolia Craib', 'ตีนนก (นครราชสีมา, ปราจีนบุรี), หางแหน (บุรีรัมย์), แหน (ทั่วไป), แหนขี้นก (เหนือ) แหนนก, แหนนา (กลาง, เหนือ), แหนปีกใหญ่ (ชลบุรี, นครสวรรค์, อุทัยธานี)', 'ไม้ต้นผลัดใบช่วงสั้น สูงถึง 20 เมตร เรือนยอดกลมและกิ่งค่อนข้างห้อยลง แตกกิ่งทางข้างชัดเจน เปลือกสีครีมถึงสีน้ำตาล แตกเป็นร่องตื้นหรือเป็นแผ่นเล็กๆ เปลือกในสีน้ำตาลเข้ม เส้นใย หนาแน่น', 'ใบเดี่ยว ออกสลับ รูปรีถึงรูปไข่กลับ ใบด้านบนสีเขียวเข้มเป็นมัน ด้านล่างเป็นนวลเกลี้ยง ทั้งสองด้าน มีต่อมกลม 1 คู่ที่กึ่งกลางหรือใกล้ปลาย กิ่งสีน้ำตาลแดงอมเข้ม ช่องอากาศจํานวนมาก', 'ดอกช่อเชิงลดไม่แตกแขนง สีครีมถึงเหลืองอ่อน ออกที่ซอกใบ กลีบเลี้ยงปลายจักเป็นพูสามเหลี่ยม ยาว 1 มิลลิเมตร มีขนแน่นด้านใน เกสรเพศผู้ยาว 5 มิลลิเมตร จานฐานดอกและรังไข่มีขนแน่น  ก้านเกสรเพศเมียยาว 2 มิลลิเมตร', 'ผลกลมหรือรูปไข่ มีปีกกว้าง 2 ปีก กว้าง 2.5-3 เซนติเมตร ยาว 2-5 เซนติเมตร ปีกแต่ละปีก กว้างมากกว่า ยาวเล็กน้อย ตัวผลกลมมีสันและร่องทั้งสองด้าน ขนกำมะหยี่', '-', 'เนื้อไม้ใช้ในงานก่อสร้างแต่ไม่คงทน', '0.0'),
(21, 'Connaraceae', 'ถอบแถบเครือ', 'Connarus semidecandrus Jack', 'กะลำเพาะ (กลาง), ขางขาว, ขางแดง, ขางน้ำครั่ง, ขี้อายเครือ (เหนือ), เครือหมาน้อ (หนองคาย) เครือไหลน้อย (เชียงราย), จำเพาะ (กลาง), ถอบแถบเครือ (กลาง), ลาโพ, หมากสง (ใต้), ลำเพาะ (ตะวันออกเฉียงใต้)', 'ไม้พุ่มรอเลื้อยหรือไม้เลื้อยเนื้อแข็ง เปลือกสีน้ำตาลอมเทาถึงสีน้ำตาลเข้ม และจะเริ่มขรุขระ นใย ลักษณะวิสัย เป็นเกล็ด เปลือกในสีชมพูถึงน้ำตาลอมแดง', 'ใบประกอบ มีใบย่อย 3 คู่ หรือใบประกอบขนนกปลายคี่ ใบย่อยรูปรีถึงรูปหอก ปลายทู่ถึงเรียวแหลม โคนรูปลิ่มถึงกลมขอบไม่จัก ใบอ่อนสีส้มอ่อน ด้านล่างสีเขียวอมเทาเกลี้ยงทั้งสองด้าน', 'ดอกช่อแตกแขนง สีขาวหรือสีอมชมพู ออกใกล้ปลายกิ่งหรือตามซอกใบบนๆ กลิ่นหอม สมบูรณ์เพศ กลีบเลี้ยง 5 กลีบ ยาว 2-4 มิลิเมตร โคนเชื่อมกันเล็กน้อย กลีบดอก 5 กลีบ รูปใบหอกแถบแถบแคบ ปลายทู่ เกสรเพศผู้ 10 อัน โคนเชื่อมกันเป็นหลอดสั้น 5 อันที่ติดตรงกับ กลีบดอกสั้นกว่าอันอื่น และมักเป็นหมัน เกสรเพศเมีย 1 อัน รังไข่มีขนแน่น ก้านเกสรเพศเมีย อาจสั้นหรือยาว', 'ผลรูปลูกแพร์หรือรี มีจงอยสั้นที่ปลาย โคนแคบคล้ายก้าน ยาว 0.5-1.5 เซนติเมตร เปลือกบาง มีขนละเอียดสีสนิม เมื่อแก่เกลี้ยงสีเหลืองอ่อน', '-', 'เนื้อไม้หยาบ ลำต้นใช้ทำเชือก ใบอ่อนต้มกินเป็นฝัก น้ำต้มรากในไทย และมาเลเซียใช้แก้เจ็บในอก และแก้ไข้ ใบและลำต้นเป็นยาถ่ายและแก้พยาธิในเด็ก', '240.0'),
(22, 'Connaraceae', 'หงอนไก่', 'Cnestis palala (Lour.) Merr', 'กระพากลาก (ตราด), กระดอลิง (หนองคาย), กะลิงปริงป่า (ราชบุรี), มะตายทากลาก (ชลบุรี) มะตายวาย, มะสักหลาด (ลำปาง), หงอนไก่, หงอนไก่ป่า (กลาง), หงอนไก่หน่วย, หมาแดง (ใต้) หมาตายไม่ต้องลาก (ชลบุรี)', 'ไม้เถาเนื้อแข็งเลื้อยพาดพันไปตามต้นไม้อื่น มีความยาวได้ถึง 10 เมตร เปลือกต้นเป็นสีเทาปนเขียว ตามกิ่งก้านและใบมีขนนุ่มสีน้ำตาลปกคลุมหนาแน่น', 'ใบประกอบแบบขนนกปลายคี่ ปลายเป็นรูปวงรี ปลายและโคนใบมน ขอบใบเรียบ ออกเรียงสลับ ใบย่อย 15-31 ใบ ส่วนท้องใบมีขนสั้นๆ ขึ้นหนาแน่น ยอดอ่อนเป็นสีแดง ก้านใบย่อยสั้นมาก', 'ดอกเป็นช่อกระจะ ช่อแยกแขนง หรือออกเป็นกระจุกตามซอกใบหรือกิ่งก้าน แกนช่อดอกมีขนนุ่ม ดอกย่อยมีจำนวนมาก กลีบเลี้ยงเชื่อมติดกัน ปลายแยกเป็นแฉกรูปขอบขนาน มีขนนุ่ม กลีบดอก สีเหลืองอ่อน รูปขอบขนาน เกสรเพศผู้ 10 อัน รังไข่อยู่เหนือวงกลีบ และมีขน', 'ผลแห้งแตกตามตะเข็บด้านเดียว สีเหลืองหรือสีน้ำตาลแดง รูปรีแกมรูปไข่ ขนสีน้ำตาลอ่อน เมล็ดสีดำลักษณะเป็นรูปวงรี ขนาดประมาณ 2 เซนติเมตร มีเนื้อหุ้มเมล็ดอยู่', '-', 'รากใช้เป็นยาบำรุงหลังคลอดบุตร เป็นยาแก้ไข้มาลาเรีย นำมาต้มกับน้ำดื่มเป็นยารักษาอาการ ปวดท้องโดยใช้ร่วมกับข้าวเม่านก ขี้อ้น มะแฟนข้าว และดูลอย เป็นยารักษาโรคทางเดินปัสสาวะ ชาวม้งจะใช้ ลำต้นหรือใบนำมาต้มกับน้ำกินเป็นยารักษาโรคนิ่ว นำมาต้มกับน้ำอาบรักษาผื่นคัน', '0.0'),
(23, 'Costaceae', 'เอื้องหมายนา', 'Cheilocostus speciosus (J. Koenig) C. D. Specht', 'ชู้ไลบ้อง ซูเลโบ (กะเหรี่ยง-แม่ฮ่องสอน), บันไดสวรรค์ เอื้องใหญ่ (ภาคใต้), เอื้องช้าง (นครศรีธรรมราช), เอื้องต้น (ยะลา), เอื้องเพ็ดม้า (ภาคกลาง)', 'พืชล้มลุก มีเหง้าอยู่ใต้ดิน ขึ้นเป็นกอๆ มีความสูงประมาณ 1-3 เมตร', 'ใบเดี่ยว ออกเวียนสลับรอบลำต้น รูปรีแกมรูปขอบขนาน หรือรูปใบหอก ปลายใบแหลม โคนใบมน เรียวเข้าหาก้านใบ ขอบใบเรียบ กาบใบอวบเป็นสีเขียวหรือสีน้ำตาลแดงโอบรอบลำต้น แผ่นใบ ค่อนข้างหนา หลังใบเรียบเป็นมัน ท้องใบมีขนนุ่ม เส้นใบออกจากเส้นกลางใบแต่จะไปสิ้นสุดที่ จุดเดียวกัน ทุกส่วนของต้นมีขน', 'ดอกเป็นช่อ ออกที่ปลายยอด ลักษณะเป็นรูปไข่หรือปากแตร ขอบหยัก เป็นคลื่น บนช่อมีกาบ สีแดงรองรับดอกแต่ละดอก เกสรเพศผู้ที่ไม่สมบูรณ์ลักษณะคล้ายกลีบดอกรูปไข่กลับสีขาวกลีบเลี้ยงสีแดงเข้ม เป็นแผ่นแบน 3 กลีบ ขอบมน', 'ผลแห้งแตก ทรงกลมหรือขอบขนานแกมรูปสามเหลี่ยม เนื้อแข็ง สุกสีแดงสด กาบหุ้มผลสีแดงเมล็ดสีดำาเป็นมัน', 'เดือนพฤษภาคม - เดือนตุลาคม', 'เหง้ารสขมเมา ขับปัสสาวะ แก้บวมน้ำ แก้ตกขาว แก้โรคติดเชื้อในทางเดินปัสสาวะ เป็นยา ถ่ายพยาธิ', '240.0'),
(24, 'Cryoteroniaceae', 'ซอม', 'Crypteronia paniculata Blume', 'กร้อม (ชุมพร), กระทงลอย (ตราด), กะอาม (อุดรธานี), กาซ้อม, ขี้มด (น่าน), คับ (เชียงใหม่) ซ้อม ซ้อม (ใต้), ปีกุย (สตูล), มุกแว (มาเลย์-สตูล), สอม (ใต้), สีดาป่า (ตะวันออก), เหมือดเซาะ (น่าน)', 'ไม้ต้น สูงถึง 30 เมตร เรือนยอดรูปกรวยคว่ำหรือทรงกระบอก เปลือกสีน้ำตาลอมเทา แตกเป็น แนวยาว เปลือกในสีแดงอ่อนอมน้ำตาล กระพี้สีขาว แก่นสีน้ำตาล', 'ใบเดี่ยว เรียงตรงข้าม รูปรีกว้างหรือรูปไข่ ปลายแหลม โคนมนแล้วสอบเรียวเข้าหาก้านใบ แผ่นใบด้านบนเกลี้ยง ด้านล่างมีขนเล็กน้อย ใบอ่อนสีม่วงเข้ม ก้านใบยาวประมาณ 1 เซนติเมตร', 'ช่อดอกแบบแยกแขนง ออกตามง่ามใบ ช่อย่อยยาวประมาณ 10 เซนติเมตร ดอกเล็กสีขาวอ่อน จนถึงสีเหลืองอ่อน กลีบเลี้ยง 5 กลีบ โคนกลีบเชื่อมติดกันเป็นรูปถ้วย ไม่มีกลีบดอก เกสรเพศผู้ 5 อัน รังไข่อยู่เหนือวงกลีบ มี 2 ช่อง ยอดเกสรตัวเมียแยกเป็น 3 แฉก', 'ผลค่อนข้างกลม ผิวแข็ง มีขนประปราย เส้นผ่าศูนย์กลางประมาณ 2 มิลลิเมตร', 'เดือนพฤศจิกายน - เดือนมกราคม ผลสุกระหว่างเดือนมกราคม - เดือนมีนาคม', 'เนื้อไม้ละเอียด แข็งพอประมาณ ตกแต่งได้ง่ายและขัดชักเงาได้ดี ใช้ทำลังใส่ของ ใช้ในการ ก่อสร้างในที่ร่มและไม่รับน้ำหนักมาก เช่น ทำพื้น ฝา', '0.0'),
(25, 'Dilleniaceae', 'ส้านใหญ่', 'Dillenia indica L', 'ส้านแข็ง ส้านต้อง (เชียงใหม่), ชะวิ่ง (ชลบุรี), ซ่าน(ภาคอีสาน)', 'ไม้ต้นผลัดใบ สูงถึง 25 เมตร ลำต้นคดงอ มีปุ่ม กิ่งก้านใหญ่แผ่กระจาย ใบกว้าง 12-20 เซนติเมตร ยาว 20-40 เซนติเมตร รูปไข่กลับ ปลายทู่หรือกลม ใบอ่อนสีน้ำตาล เส้นใบ 20-45 คู่เห็นชัด ก้านใบยาว', 'ดอกเดี่ยวหรือเป็นกลุ่ม 2-4 ดอก สีเหลืองสด ออกที่ปลายกิ่ง เมื่อบานคว่ำหน้าลง', 'ผลทรงกลม สีเหลืองอมส้ม เมื่อแก่ไม่แตก เมล็ดมีสีน้ำตาล มีเยื่อใสเป็นเมือกหุ้ม', 'เดือนกุมภาพันธ์ - เดือนพฤษภาคม', 'เปลือกต้นใช้ต้มกับน้ำดื่มเป็นยาแก้ท้องเสีย เปลือกจากเนื้อไม้ ผลมีรสเปรี้ยว ใช้ผสมปรุงเป็นยา กระจายโลหิต', '', '240.0'),
(26, 'Dilleniaceae', 'ย่านปด', 'Tetracera loureireii (Finet & Gagnep.) Pierre ex Craib', 'เถากะปูดใบเลื่อม (ประจวบคีรีขันธ์), บอระคน (ตรัง), ปัดคาย ปัดเลื่อน (สุราษฎร์ธานี), ย่านปดนครศรีธรรมราช), รสสุคนธ์ รสสุคนธ์ขาว มะตาดเครือ สุคนธรส เสาวรส (กรุงเทพฯ) ปดน้ำมัน (ปัตตานี), ปะละ สะปัลละ (นราธิวาส)', 'ไม้เถาเนื้อแข็ง เลื้อยได้ไกล 5-8 เมตร', 'ใบเดี่ยว เรียงแบบสลับกัน โคนและปลายมนถึงแหลม เนื้อใบค่อนข้างหนา มีสีเขียวเข้ม เส้นใบชัด ลักษณะคล้ายลิ้นวัว ผิวด้านบนเห็นเป็นเส้นแขนง ใต้ท้องใบสากคาย หลังใบมีสีเขียวเข้ม และ มีก้านใบยาวประมาณ 0.6-1 เซนติเมตร', 'ดอกเป็นช่อแบบแยกแขนง ออกตามซอกใบหรือปลายยอด กลิ่นหอม เกสรเพศผู้สีขาวจำนวนมาก คล้ายเส้นด้ายกระจายออกเป็นพุ่มกลมคล้ายดอกกระถิน และกลีบเลี้ยงอยู่ 5 กลีบ ส่งกลิ่นหอมแรง ในตอนกลางคืน', 'ผลเป็นรูปไข่เบี้ยว มีสีเขียว มีจะงอยที่ส่วนปลาย เมื่อแก่จะแตกแนวเดียว มีเมล็ดสีดำอยู่ 1-2 เมล็ด', 'ออกดอกเดือนธันวาคม - เดือนกุมภาพันธ์', 'ดอกช่วยบำรุงหัวใจ ช่วยแก้ลมวิงเวียน แก้อาการอ่อนเพลีย ใบนำมาต้มกับน้ำดื่มแก้อาการ สะอึก นำมาตำใช้พอกเป็นยาแก้ผดผื่นคัน', '0.0'),
(27, 'Ebenaceae', 'เนียน', 'Diospyros diepenhorstii Miq.', '-', 'ไม้ต้นเล็กถึงกลาง มีความสูงประมาณ 15-25 เมตร เปลือกสีเทาถึงน้ำตาลดำ', 'ใบเดี่ยว เรียงสลับ ขอบเรียบ เกลี้ยงหรือมีขน โคนกลมหรือกึ่งเป็นรูปหัวใจ ปลายใบแหลม เนื้อใบหนา ผิวเกลี้ยงทั้งสองด้าน เส้นใบมี 6-12 คู่', 'ดอกสีขาวถึงเหลือง ดอกเพศผู้และดอกเพศเมียออกเป็นช่อสั้นๆ ตามง่ามใบ', 'ผลกลม ผลแก่ค่อนข้างแข็งมีขนปกคลุมหนาแน่น ผิวมีสีน้ำตาลอ่อนถึงแก่ กลีบจุกผลแต่ละกลีบ เชื่อมติดกัน', '-', '-', '240.0'),
(28, 'Elaeocarpaceae', 'มะกอกน้ำ', 'Elaeocarpus hygrophilus Kurz', 'สมอพิพ่าย (ระยอง), สารภีน้ำ (ภาคกลาง), สีชัง', 'ไม้ต้น สูงได้ถึง 12 เมตร', 'ใบเป็นใบเดี่ยว รูปขอบขนานหรือรูปไข่กลับ ขอบใบหยัก โคนใบสอบ ปลายใบป้านหรือมน ผิวใบเกลี้ยง ก้านใบออกสีแดง', 'ดอกช่อ สีขาวครีม ออกตามซอกใบ ห้อยลงคล้ายระฆัง กลีบดอก 5 กลีบ รูปไข่กลับปลายกลีบ  เป็นฝอยเล็กยาวประมาณครึ่งหนึ่งของความยาวกลีบ เกสร 15-25 อัน', 'ผลทรงรียาว ผิวเรียบเกลี้ยง ปลายแหลม ก้านผลยาว 0.7-1 เซนติเมตร เมล็ดแข็งรูปกระสวย ผิวขรุขระมี 1 เมล็ด', 'ช่วงเดือนพฤษภาคม - เดือนสิงหาคม', 'ผลสุกดองน้ำเกลือ รับประทานได้ มีรสฝาดอมเปรี้ยว', '0.0'),
(29, 'Euphorbiaceae', 'โคคลาน', 'Mallotus repandus (Rottler) Mull.Arg.', 'กระเจี๊ยะ (ปัตตานี), กูเก๊าะบือยะ กูเก๊าะมูยะ (มาเลย์-นราธิวาส), แนวน้ำ (ประจวบคีรีขันธ์) โพคาน (ชัยนาท), มะกายเครือ มะปอบเครือ (ภาคเหนือ), เยี่ยวแมว (ใต้), เยี่ยวแมวเถา (นราธิวาส)', 'ไม้พุ่มรอเลื้อยหรือไม้เถาพาดพันต้นไม้อื่น ยาว 3-6 เมตร ลำต้นเป็นไม้เนื้อแข็ง เปลือกนอกสี น้ำตาลเข้ม เนื้อในสีน้ำตาลอ่อน', 'ใบเดี่ยว เรียงสลับ แผ่นใบรูปไข่แกมรูปสามเหลี่ยม รูปไข่แกมขอบขนาน หรือรูปไข่กว้าง ฐานใบ กว้างกลมปิด ขอบใบเรียบ หรือเป็นคลื่นเล็กน้อย ปลายใบแหลมหรือเรียวแหลม แผ่นใบบาง คล้ายกระดาษ หลังใบเรียบเกลี้ยง ท้องใบมีขนสั้นนุ่มสีเหลือง รูปดาว หนาแน่น หูใบรูป สามเหลี่ยม มองเห็นเส้นใบชัดเจน 3 เส้น ที่ฐานใบ', 'ดอกช่อกระจุก ออกที่ซอกใบและปลายกิ่ง ดอกย่อยหลายดอก สีขาวแกมเหลือง แยกเพศ อยู่คนละต้น ช่อดอกเพศผู้ออกที่ปลายยอด ดอกเพศผู้ออกรวมเป็นกระจุก 2-5 ดอก วงกลีบเลี้ยง แยกเป็น 3-4 พู รูปขอบขนาน มีขนนุ่ม เกสรเพศผู้ 40-75 อัน ใบประดับรูปหอก กลีบเลี้ยง 4-5 อัน รูปหอก มีขนนุ่ม', 'ผลแห้งแตกแบบแคปซูลมี 2 ห้อง รูปทรงกลมสีน้ำตาลเหลือง มีขนนุ่ม เมื่อแก่แตกตรงกลางพู เมล็ดกึ่งทรงกลม', 'เดือนมีนาคม - เดือนกันยายน', 'เถารสขม ปรุงเป็นยารับประทาน แก้ปวดเมื่อยตามร่างกาย ครั่นตัว เส้นตึง แก้ปวดหลังปวดเอว แก้กระษัย ขับปัสสาวะ แก้ไตพิการ บำรุงโลหิต แก้พิษภายใน เข้ายาแก้โรคมะเร็ง', '240.0'),
(30, 'Euphorbiaceae', 'สอยดาว', 'Mallotus paniculatus (Lam.) Mull.Arg', 'สตีตัน (เลย), สลัดบ้าง สอยดาว (ชลบุรี,ตาก), สอยดาว แสด (ใต้), หลอน (ตรัง)', 'ไม้ต้นไม่ผลัดใบ หรือผลัดใบช่วงสั้น สูงได้ถึง 10 เมตร เรือนยอดมักโปร่ง แบน แผ่กว้าง เปลือก สีเทาอ่อน เกลี้ยง ', 'ใบออกสลับ มีบางออกตรงข้าม รูปไข่กว้างหรือรูปสี่เหลี่ยมข้าวหลามตัด ปลายเรียวแหลม โคนรูปลิ่มหรือเกือบเรียบ กิ่งอ่อนมีขนขุยสีน้ำตาลแดงแน่น ใบแก่สีเขียวเข้มและเกลี้ยงด้านบน มีต่อมใหญ่ 1 คู่ใกล้โคน ด้านล่างสื่อมขาว เส้นใบข้าง 5-8 คู่ หูใบรูปสามเหลี่ยม', 'ดอกช่อใหญ่กว้างหรือห้อยลง ออกเดี่ยวที่ปลายกิ่ง ดอกเพศผู้สีขาวหรือเหลืองอ่อน กลิ่นหอม ออกเป็นช่อแตกแขนง กลีบเลี้ยง 3-4 (5) รูปไข่ถึงรูปรี เกสรเพศผู้มี 50-60 อัน ดอกเพศเมีย เป็นช่อสั้นและแตกแขนงน้อยกว่า กลีบเลี้ยงรูปกระดิ่ง มี 4-5 พู รังไข่ (2)3 ช่อง', 'กลมหรือเป็น (2)3 พูเล็กน้อย ห้อยเป็นสายยาว มีหนามกางหู 10-20 อัน ขนละเอียด เมล็ดกลม ขนาด 3 มิลลิเมตร ด่ามัน', '-', 'เนื้อไม้น้ำหนักเบามาก ใช้ทำไม้ขีดไฟ ที่ใส่ของ หรือใช้ทำพื้น เปลือกต้นใช้ทำเชือกแต่ไม่เหนียว น้ำต้มเปลือกใช้ล้างแผล รากใช้แก้ปวดศีรษะ แก้ไข้ในเด็กหรือเป็นยาบำรุงหลังคลอด', '0.0'),
(31, 'Fabaceae', 'ชุมเห็ดเทศ', 'Senna alata (L.) Roxb.', 'ชุมเห็ด ชุมเห็ดใหญ่ (ภาคกลาง), ส้มเห็ด (เชียงราย), จุมเห็ด (มหาสารคาม), ขี้คาก (ภาคเหนือ), ตะสีพอ (กะเหรี่ยง-แม่ฮ่องสอน)', 'ไม้พุ่มขนาดกลาง ความสูงของต้นประมาณ 2-3 เมตร', 'ใบประกอบแบบขนนกปลายคู่ รูปรีหรือขอบขนาน ใบย่อยมี 8-20 คู่ แกนกลางใบหนา หูใบรูปติ่งหูสามเหลี่ยม ปลายใบและโคนใบกลม แผ่นใบเกลี้ยง', 'ดอกแบบช่อกระจะช่อใหญ่ตั้ง ออกตามซอกใบและตามปลายกิ่ง สีเหลืองทอง กลีบดอก 5 กลีบ รูปไข่เกือบกลมหรือรูปซ้อน มีก้านกลีบสั้นๆ กลีบเลี้ยง 5 กลีบ เรียงซ้อนเหลี่ยมในตาดอกรูปขอขขนาน ยาวไม่เท่ากัน เกสรเพศผู้ 9-10 อัน ยาว 2 อัน สั้น 4 อัน ลกรูป 4 อัน อับเรณูเปิดที่ปลาย รังไข่เกลี้ยง มีออวูลจำนวนมาก ยอดเกสรมีขนาดเล็ก', 'ฝักรูปแถบ ยาว แบน เกลี้ยง มีปักกว้าง มีผนังกั้น เมล็ดมี 50-60 เมล็ด แบนเป็นเหลี่ยม', 'เดือนธันวาคม - เดือนกุมภาพันธ์', 'ใบนำมาชงกับน้ำดื่มเป็นยาบำรุงหัวใจ บรรเทาอาการท้องผูก ใช้เป็นยาภายนอกรักษาโรค ผิวหนัง กลาก เกลื้อน ใบและผลนำมาต้มกับน้ำรับประทานเป็นยาขับเสมหะ ฝักช่วยขับพยาธิ', '240.0'),
(32, 'Fabaceae', 'ชะเอมป่า', 'Albizia myriophylla Benth.', 'ชะเอมไทย, นะเอมป่า (กลาง), ตาลอ้อย (ตราด), เพาะซูโพ (มาฮ่องสอน), ย่านงาย (ตรัง), ส้มป่อยหวาน (เหนือ), อ้อยช้าง (สงขลา, นราธิวาส)', 'ไม้เถ้า เลื้อยพันตามต้นไม้ใหญ่ เถายาวประมาณ 30 เมตร ราก มีรสหวาน จนเอียน ไม่น่ากิน', 'ขนาดเล็กและละเอียดเป็ยฝอย คล้าย ใบส้มป่อย', 'ดอกออกเป็นช่อที่ปลายกิ่ง ลักษณะเป็นพู่ กลีบดอกสีขาว กลิ่นหอม', 'ผล หรือฝัก มีสีน้ำตาลเหลือง ตรงที่เป็น เมล็ดจะมีรอยนูนชัดเจน', 'เกือบตลอดทั้งปี', 'รากแก้กระหายน้ำ ยาระบาย ขับเสมหะ แก้ไอ เลือดออกตามไรฟัน โรคในลำคอ แก้ลม ดอกช่วยย่อยอาหาร ผลขับเสมหะ ใบขับเลือดให้ตก เนื้อไม้รสหวาน แก้โรคในลำคอ แก้ลม เลือดออกตามไรฟัน บำรุงกำลัง', '0.0'),
(33, 'Fabaceae', 'แซะย่าน', 'Aganope thyrsiflora (Benth.) Polphill', 'แซะคลาน, แซะนุ้ย (ระนอง), ตังเม, ตับเต่า (จันทบุรี)', 'ไม้เถาเนื้อแข็ง หลายปี ลำต้นทอดเลื้อยๆ ไปตามพื้นดิน หรือเกี่ยวพันไปบนต้นไม้สูง เปลือกค่อนข้างเรียบ สีน้ำตาลอ่อนแกมเทา มักแตกกิ่งก้านมาก ตามกิ่งพยช่องอากาศกระจายทั่วไป บริเวณที่ยังอ่อนใกล้ยอดมักมีขน เมื่อแก่เกลี้ยง', 'ใบประกอบขนนก ปลายคี่ เรียงเวียน ใบย่อย 5-9 ใบ เรียงตรงข้าม รูปรี รูปไข่ หรือรูปไข่กลับ ปลายเรียวแหลม โคนมนกลม ขอบเรียบ แผ่นใบด้านบนเกลี้ยงเป็นมัน ด้านล่างเกลี้ยงหรืออาจมีขนประปรายตามเส้นใบ', 'สีขาวนวลหรือแกมเขียวอ่อน อาจพบจุดสีน้ำตาลประปราย มีขน รังไข่อยู่เหนือวงกลีบมี 1 ช่อง ออวุล 3-5เม็ด ก้านยอดเกสรเพศเมียเรียว มีขน ยอดเกสรเพศเมียเล็กมาก', 'ผลแห้งไม่แตก แบน ยาวรูปขอบขนานหรือคล้ายรูปแถบยาว มีครีบตลอดความยาวของขอบทั้งสองด้าน อ่อนอาจมีขนประปราย ผลเจริญเต็มที่เกลี้ยง เมล็ดรูปคล้ายไต มี 1-4 เมล็ด', 'ออกดอกเดือนมีนาคม - เดือนพฤศจิกายน มีผลเดือนมิถุนายน - เดือนธันวาคม', 'ใช้การสกัดจากผลในการรักษาอาการโรคบิดและปวดท้อง', '240.0'),
(34, 'Fabaceae (Papillionoideae)', 'แซะ', 'Callerya atropurpurea (Wall.) Schof', 'กาแซะ, แซะ (ภาคใต้), พุงหมู (อุบลราชธานี), ยีนิเกะ (นราธิวาส)', 'ไม้ต้น ไม่ผลัดใบ สูงได้ถึง 25 เมตร เรือนยอดแน่น ลำต้นมักเป็นร่อง โคน มีพูพอนสูงชัน เปลือกสีเทาออกชมพูจนถึงน้ำตาลอ่อน เรียบหรือเป็นปล้องเล็กน้อย มีน้ำเลี้ยงเหนียวสีแดง', 'ใบประกอบขนนก ปลายคี่ ออกตรงข้าม ใบย่อย 3-5 คู่ รูปขอบขนานแกมรี ปลายเรียวแหลม โคนทู่ถึงกลม เบี้ยว สีเขียวเข้ม เนื้อคล้านหนัง เกลี้ยงทั้งสองด้าน', 'สีม่วงอมชมพูถึงสีม่วงเข้ม อาจมีสีเหลืองแต้มอยู่ด้านใน ออกเป็นช่อแตกแขนงที่ปลายกิ่งและมีช่อไม่แตกแขนงตามซอกใบ มีขนสีดำละเอียดปกคลุม กลีบเลี้ยงสีม่วงเข้ม มีขนรูประฆังเบี้ยวปลายจักรซี่ฟัน เกสรเพศเมียมี 9 อันที่เชื่อมติดกัน และอีก 1 อันแยกออกมา ขนาดยาวและสั้นสลับกัน ก้านเกสรผู้โค้ง ยาวเกือบครึ่งหนึ่งของรังไข่', 'ผลรูปไข่กลับ กว้างพองทั้งผล แก่แตก เมล็ดสีน้ำตาล มีริ้วเป็นคลื่นสีเข้มกว่า', 'เดือนมกราคม - เดือนเมษยน ผลแกหลังจากออกดอกประมาณ 2 เดือน', 'เนื้อไม้สีสวยใช้ประดับเฟอร์นิเจอร์ ทำพื้นปาเก้ พื้นกระดานและตกแต่งภายใน ใบอ่อนกิรเป็นผักกัยน้ำพริกในภาคใต้ของไทย เมล็ดโบราณใช้แก้บวม และใช้เบื่อปลา', '0.0'),
(35, 'Fabaceae', 'ชุมเห็ดไทย', 'Senna tora (L.) Roxb.', 'ชุมเห็ดควาย ชุมเห็ดนา ชุมเห็ดเล็ก (ภาคกลาง), พรมดาน (สุโขทัย), หญ้าลึกลืน หญ้าลกลีน (ปราจีนบุรี), เล็นเค็ด (มหาสารคาม), เล็บหมื่นน้อย ลับมืนน้อย เล็บน้อย (ภาคเหนือ)', 'ไม้ล้มลุกหรือไม้พุ่มขนาดเล็ก สูงประมาณ 0.3-1.3 เมตร', 'ใบประกอบแบบขนนก มีใบย่อย 3 คู่ รูปไข่กลับ ปลายใบมน มีติ่งหนาม โคนใบแหลม ขอบใบมีรอยหยักแบบขนครุย แผ่นใบสีเขียวเข้ม หลังใบเรียบเนียนไม่มีขน ท้องใบมีขนละเอียดอ่อนนุ่มปกคลุมอยู่หนาแน่น ใบมีกล่นฉุนเล็กน้อย ก้านใบมีร่องลึก ก้านมบยาว ไม่มีขน มีหูใบแบบเข็แหลมสีเขียว 2 อัน', 'ดอกเป็นช่อ ตามซอกใบและปลายกิ่ง ประมาณ 2-4 ดอก สีเหลืองสด 5 กลีบ กลีบเลี้ยง กลีบดอก สีเขียว 5 กลีบ เกสรเพศผู้จำนวน 10 อัน รังไข่เป็นเส้นยาวงอโค้งเล็กน้อยและมีขนปกคลุมปลายเกสรเพศเมียจะเป็นตุ่มสั้นๆ ก้านดอกยาว มีขนปกคลุม', 'ผลเป็นฝักยาวฏค้งเล็กน้อย แบนทั้งสองด้าน ปกคลุมไปด้วยขนอ่อนนุ่มสั้นๆ เมล็ด 20-30 เมล็ด สีน้ำตาลเหลือง สีน้ำตาล หรือสีเขียวอมเทา เมล็ดเรียบเงาเป็นมัน แข็งและแบน รสชาติขมเมามีกลิ่นเฉพาะตัว หอมเล็กน้อย', 'เดือนกุมภาพันธ์ - เดือนมิถุนายน', 'เมล็ดทำให้ง่วงนอนและหลับได้ดี เป็นยาระบายอ่อนๆ รักษาโรคผิวหนัง ทั้งต้นปรุงเป็นยาแก้ไข้ ขับพยาธิในท้องเด็กเป็นระบายอ่อนๆ และแก้ไอ แก้เสมหะ', '240.0'),
(36, 'Fabaceae', 'มะกล่ำตาหนู', 'Abrus precatorius L.', 'มะกล่ำเครือ มะกล่ำแดง กล่ำเครือ กล่ำตาไก่ มะแค๊ก (เชียงใหม่), เกมกรอม (สุรินทร์), ชะเอมเทศ ตากล่ำ (ภาคกลาง), มะขามเถา ไม้ไฟ (ตรัง), หมากกล่ำตาแดง (ภาคอีสาน)', 'ไม้เถาเลื้อยมีอายุได้หลายปี มีความสูงของต้นได้ถึง 5 เมตร', 'ใบประกอบแบบขนนก ใบย่อยมี 8-10 คู่ รูปรี ขอบใบเรียบ แผ่นใบบางสีเขียว', 'ดอกช่อแบบช่อกระจะ โดยจะออกตามซอกใบ สีชมพูแกมม่วง กลีบดอกด้านล่างแหลมเหล็ก มี 3 กลีบ', 'ผลฝักแบนยาว แก่แตกได้ เมล็ดกลมรี สีแดงหรือสีแดงสดรอบขั้วสีดำ', 'ออกดอกในช่วงเดือนกันยายน - เดือนธันวาคม', ' ใบมีรสหวาน ใช้แก้ร้อนในกระหายน้ำ ชงรับประทานแนน้ำชา ใบต้มน้ำดื่มแก้เจ็บคอ แก้หลอดลมอักเสบ แก้ตับอักเสบ ขับปัสสาวะ แก้ปวดบวมตามข้อ ตำพอก แก้ปวดบวม แก้อักเสบ เถาและ รากใช้เป็นยาแก้ร้อนในกระหายน้ำ ขับพิษร้อน แก้คออักเสบ คอเจ็บ คอบวม ขับเสมหะ แก้เสียงแห้ง แก้ไอหวัด', '0.0'),
(37, 'Fabaceae', 'มะกล่ำต้น', 'Adenanthera pavonina L.', 'มะกล่ำต้น, มะกล่ำตาช้าง (ทั่วไป), มะแค้ก (ม่ฮ่องสอน), มะแดง, มะหัวแดง, มะโหกแดง (เหนือ), หมากแค้ก, อีหลำ (อุบลราชธานี)', 'ไม้ต้นผลัดใบ สูงได้ถึง 25 เมตร เรือนยอดกลม กิ่งใหญ่กว้าง มีพูพอนเล็ก เปลือกสีเทาอมชมพูถึงสีน้ำตาล ค่อนข้างแตกเป็นแผ่น', 'ใบประกอบขนนก 2 ชั้น ชั้นที่ 1 ใบย่อย 3-6 คู่ ออกตรงข้าม ชั้นที่ 2 ใบย่อย 5-8(12) คู่ ออกสลับ รูปไข่หรือรูปขอบขนาน ปลายกลม โคนเบี้ยว ก้านใบสั้น ยอดอ่อนส่วนมากเกลี้ยง ใบแก่เนื้อบางคล้ายกระดาษ หูใบเล็กมาก ร่วงเร็ว', 'สีเหลืองอมครีม แก่เปลี่ยนรเป็นสีส้ม กลิ่นอ่อนๆ สมบูรณ์เพศ ออกเป็นช่อกลมแน่น ตามซอกใบบนหรือช่อแตกแขนงที่ปลายกิ่ง ดอกทะยอยบานออกจากโคนช่อไปยังปลาย กลีบเลี้ยงมีซี่กลมเล็ก มีขนเล็กน้อยถึงเกลี้ยง กลีบดอก 5 กลีบ เกสรเพศเมีย มี 10 อันแยกกัน ยาวกว่าเลี้ยงเล็กน้อย มีต่อมที่ปลายรังำข่ เกสรเพศผู้ยาวเท่ากัร', 'สีเขียว แก่สีน้ำตาลถึงออกดำ คล้ายสายหนังบาง โค้งเมื่อยังอ่อน แก่แตกเป็น 2 ส่วน บิดเป็นเกลียว หลังจากแตก เมล็ด กลมถึงรูปไข่กลับ หรือกึ่งรูปหัวใจ สีแดงสดไม่มีจุดดำ เกลี้ยง เป็นมัน', ' - ', 'เนื้อไม้แข็งและทนทาน แห้งเร็ว ขัดเงาสวย ใช้ทำเฟอร์นิเจอร์ชั้นดี รากใช้ย้อมผ้าสีแดง เปลือกต้นมีชาโปนินให้สระผมหรือซักผ้า', '240.0'),
(38, 'Fabaceae', 'ไพเล็ก', 'Ormosia sumatrna (Miq.) Prain', 'เผาะแผะ (นราธิวาส), ไพเล็ก (ใต้), มะกล่ำต้น (เหนือ)', 'ไม้ต้น ไม่ผลัดใบ สูงได้ถึง 25 เมตร เปฃือก สีน้ำตาลอมเทา เรียบ มีช่องอากาศแน่น', 'ใบประกอบแบบขนนก ใบย่อย 3-6(9) คู่ ออกตรงข้ามใบคู่บนขนาดใหญ่กว่ามาก รูปขอบขนานถึงรูปไข่กลับหรือใบหอกกลับปลายทู่หรือใบเรียวแหลม ใบแก่เนื้อคล้ายหนัง ด้านบนผิวเกลี้ยง ด้านล่างมีขนกำมะหยี่สีทองติดแน่น', 'ดอกเป็นช่อยาว สีขาวหรือออกชมพู ออกตามซอกใบขนๆ และที่ปลายกิ่ง แกนกลางมีขนกำมะหยี่ ก้านดอกยาว 3-4 มิลลิเมตร กลีบเลี้ยงยาว ± มิลลิเมตร ปลายจักแหลมยาวเท่ากับหลอดกลีบ กลีบดอกกลางกว้าง ± มิลลิเมตร รูปกลมปลายมีแอ่ง โคนสั้นคล้ายเป็นก้านกลีบปีกข้างและกลีบคู่ล่างกว้าง 3.5 มิลลิเมตร ยาว ± มิลลิเมตร รูปขอบขนาน รังไข่มีขนแน่น', 'ผลกว้าง 1-2.5 เซนติเมตร ยาว 2-5 เซนติเมตร สีดำ รูปรีกว้างเบี้ยว ถึงรูปขอบขนาน มีปลายสั้น ป่องตรงเมล็ด เมล็ด 1-3 เมล็ด ยาว 10 มิลลิเมตร รูปไข่แบน สีแดงเป็นมันหรือสีส้ม', ' - ', 'เนื้อไม้สวยมีน้ำหนัก สีน้ำตาลอ่อนและเป็นมัน เนื้อแข็งและแห้งเร็ว แต่ไม่ทนทาน เหมาะในการใช้ทำไม้พื้น ก่อสร้างงานเบาในที่ร่ม', '0.0'),
(39, 'Fabaceae', 'หมามุ่ย', 'Mucuna pruriens (L.) DC.', 'หมาเหยือง บะเหยือง (ภาคเหนือ), โพล่ยู (กะเหรี่ยง กาญจนบุรี), กลออื้อแซ (กะเหรี่ยง-แม่ฮ่องสอน)', ' ไม้เลื้อยล้มลุก ลำต้นเหนียวคล้ายเชือก มีความยาว 3-10 เมตร', 'รูปไข่ โคนใบกลม ตัวใบบาง และมีขนปกคลุมทั้งสองด้านของใบ', 'ดอก ออกเป็นช่อตามง่ามใบ มีกลิ่นฉุน กลีบดอกเหมือนถ้วย สีน้ำตาล มีขนปกคลุม', 'เป็นผักยาว มีความยาวประใณ 10 เซนติเมตร ปกคลุมด้วยขนละเอียดสีน้ำตาลอมแดงหรือสีทองอมแดง ขนหมามุ่ยเต็มไปด้วยสารเซโรโทนิน (Serotonin) เมื่อสัมผัสผิวจะทำให้เกิดอาการคันแพ้ระคายเคืองอย่างรุนแรงภายในมีฝักมีเมล็ดรูปไข่ 4-7 เมล็ดต่อหนึ่งฝัก เมล็ดมีสีน้ำตาลเข้มหรือสีดำ', ' - ', 'ใช้เป็นยาบำรุงกำลัง ทำให้ไม่เหนื่อยง่าย ช่วยเพิ่มสมรรถภาพทางเพศ เมล็ดช่วยรักษาภาวะการมีบุตรยากทั้งชายและหญิง', '240.0'),
(40, 'Fabaceae', 'อะราง', 'Peltophorum dasyrrhachis (Miq.) Kurz', 'ช้าขม จ๊าขาม (เลย), ร้าง อะล้าง (นครราชสีมา, อุดรธานี), อินทรี (จันทบุรี), คางรุง คางฮุ่ง (พิษณุโลก), กว่าแซก (เขมร-กาญจนบุรี), ตาแซก (เขมร-บุรีรีมย์), ราง (ส่วย-สุรินทร์), นนทรี (ภาคกลาง) ', 'ไม้ยืนต้นผลัดใบขนาดกลางถึงใหญ่ สูงได้ถึง 30 เมตร', 'ใบประกอบแบบขนนกสองชั้นออกเรียงสลับกัน ใบย่อย 6-18 คู่ รูปขอบขนาน ปลายใบมนเว้าตื้นตรงกลาง ดคนใบมนเบี้ยว ขอบใบเรียบ ผิวใบด้านบนเกลี้ยงและเป็นมัน ท้องใบมีขนสีน้ำตาลอยู่ประปราย ใบย่อยไม่มีก้านใบ', 'ดอกช่อแบบห้อยระย้าลงสู่พื้นดิน ออกตามซอกใบ ดอกย่อยสัเหลืองตูมเป็นรูปไข่ กลีบเลี้ยง 5 กลีบ กลีบดอก 5 กลีบ ปลายมน โคนเรียว บานเต็มที่จะมีขนาดกว้างประมาณ 1.8-2 เซนติเมตร', 'ฝักแห้งแก่ไม่แตก รูปขแบขนาน เปลือกสีน้ำตาลแดง เมล็ดเรียงตามขวางมี 4-8 เมล็ดต่อฝัก', 'เดือนมกราคม - เดือนมีนาคม เริ่มติดผลเดือนเมษายน - เดืแนพฤษภาคม', 'ไม่ได้ระบุประเภทการใช้ประโยชน์', '0.0'),
(41, 'Flagellariaceae', 'หวายลิง', 'Flagellaria indica L.', 'หวายเย็บจาก หวายลี (ภาคใต้)', 'ไม้เถาเลื้อย กลมเกลี้ยง ลำต้นหรือเนื้อไม้แข็งเหนียว ผิวเปลือกเนียนเป็นมัน', 'ใบเดี่ยว เรียงสลับแบบห่างๆ รูปยาวรีหรือรูปหอกแคบ ปลายใบแหลมเรียวม้วนเป็นมือไว้เกาะโคนมน ขอบเรียบ เนื้อใบบางคล้ายแผ่นหนัง ผิวใบเกลี้ยงทั้งสองด้าน หลังใบสีเขียวเข้มมันวาวท้องใบมีสีซีดกว่า เส้นใบขนานตามความยาวของใบ ก้านใบสั้นหรือไม่มีก้าน กาบใบเป็นหลอด', 'ดอกช่อ ออกจากปลายยอด แต่ละช่อประกอบไปด้วยดอกย่อยขนาดเล็กจำนวนมาก ดอกย่อยเป็นสีขาวครีมหรือสีขาวอมเหลือง ไม่มีก้านดอก วงกลีบรวมมี 6 กลีบ เป็นรูปรี แยกจากกันเป็นอิสระ เรียงเป็น 2 วง กลีบวงนอกจะยาวกว่ากลีบวงใน เกสรเพศผู้ 6 อัน ก้านเกสรเพศเมียปลายแยกเป็นแฉก โผล่พ้นกลีบรวม', 'ผลขนาดเล็กกลม เป็นพวงคล้ายกับเมล็ดพริกไทย มีหลายช่อในก้านเดียวกัน ผิวเรียบ ผนังชั้นนอกบาง ผนังชั้นในแข็ง ปลายผลมีตุแหลม อ่อนเป็นสีเขียว เปลี่ยนเป็นสีเหลือง แก่จัดสัส้มสดหรือสีชมพูอมแดง เมล็ด 1 เมล็ด หนึ่งก้านจะมีผลประมาณ 30-40 ผล', ' - ', 'ต้นและหง้า นำมาต้มดื่มเป็นยาขับปัสสาวะ เถาแก้ไข้ตัวร้อน ใบเป็นยาฝาดสมาน ใช้รักษาบาดแผล ยอดอ่อนรำมาใช้สระผม', '240.0'),
(42, 'Gentianaceae', 'ทุ่มบก', 'Fagraea racemosa Jack', 'ตะเคียนเฒ่า (ตราด), พวาน้ำ (ชุมพร, ปัตตานี), ทุ่มบก (นครศรีธรรมราช), หว้าน้ำ (ภาคใต้), ปูเละ (มาเลย์-นราธิวาส)', 'ไม้ต้นขนาดเล็ก ไม่ผลัดใบ สูงได้ถึง 12 เมตร เรือนยอดเป็นรูปโคนเปิด เปลิอกสีครีมจนสีน้ำตาล', 'ใบเดี่ยว รูปขอบขนานแกมรีถึงรูปไข่กลับ หรือรูปไข่แคบปลายเรียวแหลม โคนกลม ใบแก่เนื้อนุ่มหรือหนาคล้ายหนัง สีเขียวมันด้านบน เกลี้ยงทั้งสองด้าน มีหูใบเชื่อมกันเป็นเยื่อบางรอบกิ่ง', 'ดอกช่อเชิงลด สัขาวครีม สีส้มอ่อน ถึงสีชมู ก้านช่อดอกยาว 2-8 เซนติเมตร ก้านดอกยาว 2-5 มิลลิเมตร กลีบเลี้ยงยาว 5-7 มิลลิเมตร รูปกระดิ่ง กลีบดอกรูปกรวย เกสรเพศผู้ยาว 2.2 เซนติเมตร อับเรณูยาว ±4 มิลลิเมตร ติดที่ครึ่งบนของหลอดกลีบดอก รังไข่และก้านเกสรตัวเมียยาว 2-3 เซนติเมตร ยอดเกสรตัวเมียขนาดเล็ก', 'ผลรูปไข่หรือรูปรี มีก้านเกสรตัวเมียติดทนที่ปลาย ปลายแหลมสั้น สีเหลืองอ่อน ด้านนอกหนามัน มีเนื้อด้านใน เมล็ดเล็กจำนวนมาก', ' - ', 'ใช้ก่อสร้างหรือทฟืน น้ำต้มใบ เปลือกต้นและราก ใช้เป็นยาบำรุงในยาโบราณขแงมาเลเซีย ในฟิลิปปินส์ เปลือกต้นและดอกใช้ถอนพิษงู', '0.0'),
(43, 'Gentaceae', 'เมื่อย', 'Gnetum montanum Markgr.', 'เมื่อย (ตราด), ม่วย (เชียงราย อุบลราชธานี), มะม่วย (เชียงใหม่), แฮนม่วย (เลย)', 'ไม้เถาเนื้องแข็ง ยาวกว่า 10 เมตร', 'ใบเดี่ยว เรียงตรงข้ามหรือตรงข้ามสลับตั้งฉาก รูปไข่ถึงรูปไข่แกมรูปขอบขนาน ใบมีหลายขนาด กว้างได้ถึง 11 เซนติเมตร ยาวได้ถึง 25 เซนติเมตร ปลายแหลมหรือเรียวแหลม โคนมนกลม หรือสอบขอบเรียบ แผ่นใบหน้าคล้ายหนังหรือกึ่งคล้ายแผ่นหนัง สีดำเมื่อแห้ง', 'สตรอบิลัสออกเป็ยช่อหรือช่อเชิงลง ออกตามลำต้นหรือปลายกิ่ง มักแตกแขนงมาก ที่โคนช่อมีเกล็ด 2 อัน เรียงตรงข้าม ประกอบด้วย คอลลาร์ลักษณธคล้ายวงแหวนหลายอันที่มีขนคล้ายสายลูฟปัดสตรอบิลัสแยกเพศ อยู่ต่างต้น สตรอละสเพศผู้ กว้างประมาณ 4 มิลลิเมตร ยาวประมาณ 3 เซนติเมตร ลักษณธคล้ายวงแหวน วงกลีบรวมรูปอุ้งเล็บ แตกตามขวางเกสรเพศผู้ 1 อัน รังไข่ที่ไม่เจริญ รูปไข่ดอกเพศเมียมี 5-7 ดอก ไม่มีวงกลับรวม ออวุลมีชั้นบางๆคลุม 2-3 ชั้น', 'เมล็ดทรงรี กว้างประมาณ 1 เซนติเมตร ยาวประมาณ 1.5 เซนติเมตร เมื่อสุกสีแดง ก้านอ้วน สั้นยาวประมาณ 2 มิลลิเมตร', 'ออกดอกช่วงเดือนกุมภาพันธ์ - เดือนเมษายน เมล็ดแก่ช่วงเดือนมีนาคม - เดือนมิถุนายน', 'ใบใช้ทำเมี่ยงใบชา ยาเบื่อปลา', '240.0'),
(44, 'Hypoxidaceae', 'พร้าวนกคุ่ม', 'Molineria latifolia (Deyand. Ex W. T. Aiton) Herb.', 'พร้าวนก (นครศรีธรรมราช), จ๊าลาน (เชียงใหม่), ละโมยอ (มาเลย์ฯ-นราธิวาส), ว่านสากเหล็ก (ภาคกลาง)', 'ไม้ล้มลุก  ลักษณธคล้ายพืชพวกปาล์ม', 'ใบเรียงสลับติดกันที่โคนต้น แผ่นใบรูปขอบขนานแกมรูปหอก พับเป็นร่องๆ ตามยาว คล้ายใบปาล์ม ปลายใบเรียวแหลม โคนใบสอบแคบ โคนแผ่กว้างหุ้มลำต้น', 'ดอก มี 6 กลีบ สีเหลือง โคนเชื่อมติดกัน ออกรวมกันแน่น เป็นช่อรูปทรงกระบอกปลายแหลม', 'ผลแก่สีขาวถึงแดง ขนาดยาวประมาณ 4-5 เซนติเมตร ส่วนที่ด้านขั้วป่อง ออกเส้นผ่าศูนย์กลาง ประมาณ 1 เซนติเมตร และค่อยๆ เรียวไปทางปลายผล', ' - ', 'ใบ ยอด ลำต้น ราก แก้ไข้ น้ำต้มจากดอกและราก ช่วยให้เจริญอาหาร ขับปัสสาวะผลสุกกินได้รสหวานแกมเปรี้ยว', '0.0'),
(45, 'Hypoxidaceae', 'ติ้ว', 'cratoxylum formosum (Jack) Benth. & Hook. Ex Dyer', 'ติ้วขาว (กรุงเทพฯ), ติ้วส้ม (นครราชสีมา), แต้วหอม (พิษณุโลก), มูโต๊ะ (มาเลย์ฯ-นราธิวาส), สมอตีน, นน (ใต้), สมอต้นนกใต้', 'ไม้พุ่มหรือไม้ต้น ผลัดใบ อาจสูงได้ถึง20 เมตร ต้นอ่อนมีหนาม เปลือกแตกเป็นสะเก็ดหนา สีน้ำตาลดำ กิ่งเกลี้ยง', 'ใบเดี่ยว เรียงตรงข้าม รูปรี รูปขอบขนาน รูปขนานแกมรูปหอก ปลายแหลมมนหรือกลม โคนสอบหรือมน ขอบเรียบ แผ่นใบบางคล้ายกระดาษ ผิวด้านบนมีขนสาก ด้านล่างมีนวล มีขนสั้นหนานุ่ม ใบอ่อนสีแดงอมชมพู', 'ดอกเดี่ยว หรือเป็นช่อแบบกระจุก ช่อกระจะหรือช่อแยกแขนงสั้นๆ ออกที่ซอกใบและที่ปลายกิ่ง ออกพร้อมแตกใบใหม่ ใบแระดับขนาดเล็ก ร่วงง่าย กลีบเลี้ยง 5 ดลีบ รูปไข่ แกมรูปหอกกลีบดอก 5กลีบ แยกกันสีขาวหรือชมพูอ่อน รูปไข่แกมรูปหอก ขอบมีขนยาว เกสรเพศผู้เชื่อมติดกันเป็นกลุ่ม 3 กลุ่ม รังไข่มี 3 ช่อง เกสรเพศผู้จำนวนมากเชื่อมติดกันเป็น 3 กลุ่ม สลับกับเกสรเพศผู้ที่ไม่สมบูรณ์ 3 อัน แยกจากกัน', 'ผลแห้งแตก รูปรีแกมรูปขอบขนาน ผิวมีนวล กลับเลี้ยงติดทนหุ้มเฉพาะส่วนโคนของผล ผลแก่แตกตามรอยประสานเป็น 3 ซีก เมล็ดจำนวนมาก เมล็ดรวมปีกรูปขอบขนานแกมรูปไข่กลับ', 'ออกดอกและติดผลระหว่างเดือนมกราคม - เดือนมิถุนายน', 'เนื้อไม้ใช้ในการก่อสร้าง ทำรั้ว ทำฟืนและถ่าน', '240.0'),
(46, 'Lamiaceae', 'ตีนนก ไม้นนท์', 'Vitex pinnata L.', 'กะพุน, ตะพรุน (ชลบุรี), กานน (ประจวบคีรีขันธ์), กาสามปี (เหนือ), ไข่เน่า (ลพบุรี), ตีนนก (ทั่วไป), ตะพุ่ม (ตราด), สมอตีน, นน (ใต้), สมอต้นนกใต้', 'ไม้พุ่ม สูง 15-20 เมตร กิ่งอ่อนเป็นสี่เหลี่ยม มีขนนุ่มประปราย', 'ใบปรกอบแบบนิ้วมือ เรียงตรงข้ามสลับ มี 3-5 ใบย่อย รูปรีหรือรูปไข่กลับ ปลายแหลมหรือในโคนสอบเรียบหรือรูปลิ่ม ขอบเรียบใบย่อยตรงกลางมักมีขนาดใหญ่ ใบย่อยคู่ล่างมักมีขนาดเล็กกว่า แผ่นใบบางคล้ายกระดาษ ด้านบนและด้านล่างผิวเกลี้ยง', 'ดอกช่อแบบช่อแยกแขนงรูปพีระมิดแน่น ออกที่ปลายกิ่ง ใบประดับย่อยจำนวนมาก รูปใบหอกหรือรูปแถบแกมขอบคูขนาน กลีบเลี้ยง 5 กลีบ โคนเชื่อมติดกันเป็นรูประฆังหรือรูปถ้วย ปลายแยกเป็นติ่งรูปสามเหลี่ยม ผิวด้านนอกมีขนสั้นด้านในเกลี้ยง กลีบดอกโคนเชื่อมติดกันเป็นหลอดรูปกรวยสีม่วงอมน้ำเงิน ปลายหลอดแยกเป็น 5 แฉก เกสรเพศผู้ 4 อัน รังไข่อยู่เหนือวงกลีบรูปเกือบกลม คาร์เพล 2 คาร์เพลเชื่อมติดกัน มีออวุล 2 เม็ด ก้านยอดเกสรเพศเมียปลายแฉกขนาดไม่เท่ากัน', 'ผลแบบผนังชั้นในแข็ง รูปร่างค่อนข้างกลม ผลอ่อนสีเขียว ผลแก่สีม่วงเข้มหรือดำ ด้านล่างติดกับก้านผลมีส่วนประกอบของกลีบเลี้ยงรูปถ้วยหรือกลมแบนติดทน', 'ออกดอกช่วงเดือนมีนาคม - เดือนกันยายน', 'เนื้อไม้ใช้ในงานก่อสร้าง เรือ เครื่องมือและอุปกรณ์ทำเชื้อเพลิง ใบและเปลือกใช้เป็นยาเฉพาะที่แก้ปวดท้อง แก้ไข้และรักษาบาดแผล', '0.0'),
(47, 'Lauraceae', 'หมีเหม็น', 'litsea glutinisa (Lour.) C.B. Rob.', 'หมูเหม็น (แพร่), อีเหม็น (กาญจนบุรี, ราชบุรี), หมี (ลำปาง, อุดรธานี), หมูทะลวง (จันทรบุรี), ดอกจุ๋ม (ลำปาง), ตังสีไพร (พิษณุโลก), ทังบวน (ปัตตานี), มัน (ตรัง), มะเย้อ ยุบเหยา (ภาคเหนือ, ชลบุรี), มือเบาะ (มาเลย์ฯ-ยะลา)', 'ไม้ต้น สูงประมาณ 5-15 เมตร เปลือกสีเทาปนน้ำตาล แตกเป็นสะเก็ดสี่เหลี่ยมเล็กๆ', 'ใบเดี่ยว เรียงสลับ รูปวงรีแกมขอบขนานหรือรูปไข่กลับ ฐานใบมน ปลายใบมน ขอบใบเรียบ แผ่นในค่อนข้างหนาและเหนียว แผ่นใบทั้งสองด้านมีขน ใบมีกลิ่น', 'ออกตามง่ามใบเป็นช่อ แบบซี่ร่ม ดอกแยกเพศอยู่คนละต้นหรือร่วมต้น ดอกเพศผู้ออกเป็นจุกที่ซอกใบ 2-4 ดอก กลีบเลี้ยง 4 กลีบ แยกกัน กลีบดอกมีหลายกลีบ เกสรเพศผู้จำนวนมาก เกสรเพศเมีย รังไข่อยู่เหนือวงกลีบ', 'ผลสด รูปทรงกลม เมล็ดเดียว แข็ง', 'เดือนกรกฎาคม - เดือนตุลาคม', 'รากเป็นยาฝาดสมาน และยาบพรุง ใบเป็นยาฝาดสมาน แก้อาการระคายเคืองของผิวหนังตำเป็นยาพอกบาดแผลเล็กๆ ผลกินได้และให้น้ำมัน เป็นยาถูนวดแก้ปวด', '240.0'),
(48, 'Lamiaceae', 'นมสวรรค์', 'Clerodendrum paniculatum L.', 'ฉัตรฟ้า สาวสวรรค์ (นครราชสีมา), ปิ้งจงวา (เขมร-สุรินท์), พนมสวรรค์ (ภาคใต้), พวงพีเหลือง (เลย), พู่หมวก (กนุงเทพฯ), หัวลิง (สระบุรี)', 'ไม้พุ่มสูง ลำต้นตรง มีความสูงได้ถึง 3 เมตร เปลือกต้นเรียบ สีน้ำตาลแกมเขียว', 'ใบเดี่ยวออกเรียงตรงข้ามสลับตั้งฉาก รูปฝ่ามือรูปไข่กว้างหรือรูปข่เกือบกลม ขอบใบหยักเว้าลึกเป็นแฉก 3-7 แฉก ฐานใบเป็นรูปหัวใจ ขอบใบเป็นจักคล้ายฟันเลื่อย ผิวใบมีขนและต่อมกระจายอยู่ทั้ง 2 ด้าน หลังใบเรียบมีสีเขียวเข้ม ท้องใบมีขนสากระคายมือและมีสีอ่อนกว่า ก้านใบยาว', 'ดอกเป็นช่อแบบแยกแขนงขนาดใหญ่ที่ปลายยอด สีแดงหรือสีส้ม ช่อดอกเป็นชั้นคล้ายฉัตรหรือรูปไข่ หลับดอกเชื่อมติดกันเป็นหลอดยาว ปลายแยหเป็น 5 กลีบ ดอกมี 2 ชนิด คือ ชนิดดอกขาว เรียกว่า \"นมสวรรค์ตัวผู้\" และอีกชนิดสีแดง เรียกว่า \"นมสวรรค์ตัวเมีย\"', 'ผลมรลักษณะกลม ขนาดเล็กสีเขียว ผนังชั้นในแข็ง ทรงกลม มี 2-4 พู เมื่สุกแล้วจะเปลี่ยนเป็นสีน้ำเงินแกมเขียวหรือสีดำ มีเมล็ดเดียว แข็ง', 'เดือนพฤษภาคม - เดือนธันวาคม', 'ใช้รักษาอาการแน่นหน้าอก ดอกแก้ตกเลือด แก้พิษสัตว์กัดต่อย และพิษที่เกิดจากการติดเชื้อ รากแก้ไข้มาลาเรีย ชับลม แก้วัณโรค ทั้งต้นแก้พิษตะขาบ และแมลงป่องต่อย แก้พิษฝีฝักบัว', '0.0'),
(49, 'Lecythidaceae', 'จิกนม', 'Barringtonia macrostachya (Jack) Kurz', 'จิกนม (ใต้), จิกนุ่ม (นครศรีธรรมราช), นมยาน (หนองคาย)', 'ไม้ยืนต้นขนาดเล็กจนถึงขนาดกลาง มีความสูงประมาณ 4-20 เมตร', 'ใบเป็นใบเดี่ยว ออกเรียงสลับกันไปตามข้อต้น รูปยาวรี ปลายใบและโคนใบแหลม ขอบใบมีจักเล็กๆ ทั่วทั้งใบ เนื้อใบบางคล้ายกระดาษสีเขียว', 'ดอกออกเป็นช่อตรงส่วนย่อดของต้น ช่อดอกมีลักษณะห้อยลง ช่อยาวประมาณ 9-18 นิ้ว ในแต่ละช่อดอกจะประกอบไปด้วยดอกเป็นจำนวนมาก สีแดง สัชมพูเข้ม หรือสีเขียวขอบแดงมี 4 กลีบ กลีบรองดอกเชื่อมติดกันเป็นรูปถ้วย มี 4 กลีบ เป็นสีแดง แดงเข้มหรือสีม่วงตรงฐานดอกจะมีร่องลึกเป็นวงอยู่ เกสรเป็นจำนวนมากอยู่ในดอก', 'ผลรูปมนรีและมีสัน 4 สันตามแนวยาว สีเขียว เมล็ดเป็นรูปไข่', ' - ', 'รากนำมาบดเป็นผงใช้ทาแก้ขี้กลาก ใบนำมาต้มกับน้ำดื่มเป็นยารักษาโรคแก้ปวดท้อง', '240.0'),
(50, 'Lygodiaceae', 'ลิเภายุ่ง', 'Lygodium microphyllum (Cav.) R. Br.', 'กะฉอดหนู (จันทบุรี, ตราด), รีบูปาดี ลิบูชือนิง (มลายู-นราธิวาส)', 'เป็นเฟิร์นทอดเลื้อยพาดพันกับต้นไม้อื่น ลำต้นเป็นเหง้าสั้นมีขนสีน้ำตาลเข้มหนาแน่น', 'ใบประกอบแบบขนนกสองชั้น ก้านเรียวเล็กคล้ายเส้นลวด ปลายก้านมีขนน้ำตาล ด๕นก้านใบมีสีน้ำตาล ด้านบนสีเหลืองน้ำตาล มีปีกแผ่ยื่นออกมาไม่ชัดเจนหรือไม่มี ใบย่อยออกเรียงบนแกนกลางของใบ แผ่นใบรูปหอก ปลายมนหรือแหบม ดคนรูปหัวใจ ขอบใบหยักเว้าเป็นฟันปลา ผิวใบมีขนใส หลังใบเกลี้ยงท้องใบมีขนขึ้นประปรายตามเส้นใบ กลุ่มสปอร์เกิดที่ขอบใบย่อย', ' - ', ' - ', ' - ', ' - ', '0.0'),
(51, 'Lythraceae', 'อินทนิลน้ำ', 'Lagerstroemia speciosa (L.) Pers.', 'ฉ่องมู ฉ่องพนา (กะเหรี่ยง-กาญจนบุรี), ตะแบกดำ (กรุงเทพฯ), บางอบะชา (นราธิวาส), บาเย บาเอ (มลายู-ปัตตานี), อินทนิล inthanin (ภาคกลาง, ภาคใต้)', 'ไม้ยืนต้นขนาดกลางถึงใหญ่ มีความสูงประมาณ 7-20 เมตร เปลือกต้นเรียบ สีเทา หรือ สีน้ำตาลอ่อน และมีรอยด่างเป็นดวงสีขาว', 'ใบเดี่ยว เรียงตรงข้าม รูปรีหรือรูปรีแกมขอบขนาน ปลายใบแหลม ยาวเป็นติ่ง ขอบใบเรียบหรือเป็นคลื่นเล็กน้อย โคนใบกลมหรือแหลม ผิวใบเกลี้ยง ใบมีสีเขียว เนื้อใบค่อนข้างหนาและเกลี้ยงเป็นมันทั้งสองด้าน', 'ดอกรวมกันเป็นช่อใหญ่ตามปลายกิ่งหรือตามง่ามใบตอนใกล้ๆปลายกิ่ง สีม่วงหรือม่วงอมชมพูดอกตูมจะมีตุ่มกลมๆ เล็กๆ ตั้งอยู่ตรง กลีบดอกบาง รูปซ้อนที่มีดคนกลีบเป็นก้านยาวเรียว ผิวกลีบจะเป็นคลื่นๆ เล็กน้อย บานเต็มที่จะมีรัศมีความกว้าง 5 เซยติเมตร กลีบดอก 6 กลีบ ไม่ติดกัน รูปกลม รังไข่กลมเกลี้ยง', 'ผลแห้งรูปไข่เกลี้ยง ผิวเรียบ ไม่มีขนปกคลุม สีน้ำตาลแดง ผลเป็นผลแห้ง เมื่อแก่จะแยกออกเป็น 6 ช่อง และจะเผยให้เห็นเมล็ดเล็กๆ ที่มีปีกเป็นครีบบางๆ ทางด้านบน', 'เดือนมีนาคม - เดือนตุลาคม', 'เปลือกรสฝาดขม แก้ไข้ แก้ท้องเสีย ใบรสจืดขมฝาดเย็น', '240.0'),
(52, 'Lygodiaceae', 'ลิเภาใหญ่', 'Lygodium salicifolium C. Presl', 'กะฉอด หมอยแม่หมาย (ราชบุรี), กูดคือ (น่านจ, ตีนตะขาบ (พิจิตร), รีบูบาซา ลอบูบือซา (มลายู-นราธิวาส), ลิเภา สายพานผี อู่ตะเภา (ภาคใต้), หญ้ายายเภา (จันทบุรี)', 'เฟิร์นทอดเลื้อยพาดพันกับต้นไม้อื่น ลำต้นเป็นเหง้าสั้นมีขนสีน้ำตาลเข้มหนาแน่น', 'ใบประกอบแบบขนนกสองชั้น โคนก้านใบสีน้ำตาล ด้านบนสีเหลืองน้ำตาล มีขนสีน้ำตาล มีปีกแผ่ยื่นออกมาไม่ชัดเจนหรือไม่มี ใบย่อยออกเรียงบนแกนกลางของใบ แผ่นใบเป็นรูปหอก ปลายใบมนหรือแหลม โคนใบรูปหัวใจ ขอบใบหยักเว้าเป็นฟันปลา ผิวใบมีขนใส หลังใบเกลี้ยง ท้องใบมีขนขึ้นประปรายตามเส้นใบ กลุ่มสปอร์เกิดที่ขอบใบย่อย', ' - ', ' - ', ' - ', 'ทั้งต้นมีรสจืด เป็นยาขับเสมหะ รากใช้ต้มกับน้ำดื่มเป็นยาแก้อาการร้อนใน ใบนำมาขยี้ใช้พอกแผล ช่วยห้ามเลือด หรือใช้พอกแผลสด ช่วยทำให้แผลแห้งและหายเร็วขึ้น', '0.0');
